// preparing bboxes
function preprocess_bboxes(payload) {
    function getObjectData(object_data) {
        var current_object_data = {};
        if (object_data.hasOwnProperty("type")) {
            current_object_data["type"] = object_data["type"];
        }
        if (object_data.hasOwnProperty("color")) {
            current_object_data["color"] = object_data["color"];
        }
        if (object_data.hasOwnProperty("license_plate")) {
            current_object_data["license_plate"] = object_data["license_plate"];
        }
        if (object_data.hasOwnProperty("id")) {
            current_object_data["id"] = object_data["id"];
        }
        if (object_data.hasOwnProperty("roi_type")) {
            current_object_data["roi_type"] = object_data["roi_type"];
        }
        return current_object_data;
    }
    function getLotBbox(lot_data) {
        return { "vertex1": lot_data.vertex1, "vertex2": lot_data.vertex2, "vertex3": lot_data.vertex3, "vertex4": lot_data.vertex4, "vertex_0": lot_data.vertex1 };
    }

    var results = {};
    var objects = [];
    var objects_extra = [];
    var lots = [];
    // get all objects bboxes
    for (let key in payload) {
        if (payload.hasOwnProperty(key)) {
            var objectData = getObjectData(payload[key]);
            if (objectData !== null) {
                objects_extra.push(
                    objectData
                );
                objects.push(
                    payload[key]["bbox"]
                );
            }
        }
    }
    // get all lots bboxes
    var predefined_regions = flow.get(region_name);
    var keys = Object.keys(predefined_regions);

    for (let i = 0; i < keys.length; i++) {
        if (predefined_regions.hasOwnProperty(keys[i]) && keys[i] != "_msgid") {
            lots.push(
                getLotBbox(predefined_regions[keys[i]])
            );
        }
    }

    results["objects"] = objects;
    results["objects_extra"] = objects_extra;
    results["lots"] = lots;
    return results;
}

// euclidean distance and iou check
function euclidean_iou_function(payload, distance_threshold = 100, intersection_type = "region", intersection_threshold = 0.5, object_counter = false) {
    function getBboxCentroid(bbox) {
        var centroid_x = (bbox["vertex1"].x + bbox["vertex2"].x + bbox["vertex3"].x + bbox["vertex4"].x) / 4;
        var centroid_y = (bbox["vertex1"].y + bbox["vertex2"].y + bbox["vertex3"].y + bbox["vertex4"].y) / 4;
        return [centroid_x, centroid_y];
    }
    function calculateEuclideanDistance(centroid1, centroid2) {
        return Math.sqrt((centroid2[0] - centroid1[0]) ** 2 + (centroid2[1] - centroid1[1]) ** 2);
    }
    function calculateIntersectArea(bbox1, bbox2) { // objects, lots
        var bbox1_key = Object.keys(bbox1)
        var bbox2_key = Object.keys(bbox2)
        var intersection = [];

        for (let i = 0; i < bbox1_key.length - 1; i++) {
            for (let j = 0; j < bbox2_key.length - 1; j++) {
                var A1 = bbox1[bbox1_key[i + 1]].y - bbox1[bbox1_key[i]].y; // Ax + By + C = 0
                var B1 = bbox1[bbox1_key[i]].x - bbox1[bbox1_key[i + 1]].x;
                var C1 = - A1 * bbox1[bbox1_key[i]].x - B1 * bbox1[bbox1_key[i]].y;

                var A2 = bbox2[bbox2_key[j + 1]].y - bbox2[bbox2_key[j]].y;
                var B2 = bbox2[bbox2_key[j]].x - bbox2[bbox2_key[j + 1]].x;
                var C2 = - A2 * bbox2[bbox2_key[j]].x - B2 * bbox2[bbox2_key[j]].y;

                // parallel lines case
                if (A1 * B2 == A2 * B1) {
                    // coincide case
                    if ((A1 * C2 == A2 * C1) && (B1 * C2 == B2 * C1)) {
                        if (B1 == 0 && A1 == 0) { // one point bbox case
                            intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                        }
                        else if (B2 == 0 && A2 == 0) { // one point bbox case
                            intersection.push([bbox2[bbox2_key[i]].x.toFixed(6), bbox2[bbox2_key[i]].y.toFixed(6)]);
                        }
                        else if (B1 == 0) { // vertical lines case
                            var y_min1 = (bbox1[bbox1_key[i]].y < bbox1[bbox1_key[i + 1]].y) ? bbox1[bbox1_key[i]].y : bbox1[bbox1_key[i + 1]].y;
                            var y_max1 = (bbox1[bbox1_key[i]].y < bbox1[bbox1_key[i + 1]].y) ? bbox1[bbox1_key[i + 1]].y : bbox1[bbox1_key[i]].y;
                            var y_min2 = (bbox2[bbox2_key[i]].y < bbox2[bbox2_key[i + 1]].y) ? bbox2[bbox2_key[i]].y : bbox2[bbox2_key[i + 1]].y;
                            var y_max2 = (bbox2[bbox2_key[i]].y < bbox2[bbox2_key[i + 1]].y) ? bbox2[bbox2_key[i + 1]].y : bbox2[bbox2_key[i]].y;
                            if ((y_min1 < y_max2) && (y_min2 < y_max1)) {
                                var y_min = (y_min1 < y_min2) ? y_min2 : y_min1;
                                var y_max = (y_max1 < y_max2) ? y_max1 : y_max2;
                                intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), y_min.toFixed(6)]);
                                intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), y_max.toFixed(6)]);
                            }
                        }
                        else if (A1 == 0) { // horizontal lines case
                            var x_min1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i]].x : bbox1[bbox1_key[i + 1]].x;
                            var x_max1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i + 1]].x : bbox1[bbox1_key[i]].x;
                            var x_min2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i]].x : bbox2[bbox2_key[i + 1]].x;
                            var x_max2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i + 1]].x : bbox2[bbox2_key[i]].x;
                            if ((x_min1 < x_max2) && (x_min2 < x_max1)) {
                                var x_min = (x_min1 < x_min2) ? x_min2 : x_min1;
                                var x_max = (x_max1 < x_max2) ? x_max1 : x_max2;
                                intersection.push([x_min.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                                intersection.push([x_max.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                            }
                        }
                        else {
                            var x_min1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i]].x : bbox1[bbox1_key[i + 1]].x;
                            var x_max1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i + 1]].x : bbox1[bbox1_key[i]].x;
                            var x_min2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i]].x : bbox2[bbox2_key[i + 1]].x;
                            var x_max2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i + 1]].x : bbox2[bbox2_key[i]].x;
                            if ((x_min1 < x_max2) && (x_min2 < x_max1) && (y_min1 < y_max2) && (y_min2 < y_max1)) {
                                var x_min = (x_min1 < x_min2) ? x_min2 : x_min1;
                                var x_max = (x_max1 < x_max2) ? x_max1 : x_max2;
                                var xY_min = (-A1 * x_min - C1) / B1
                                var xY_max = (-A1 * x_max - C1) / B1
                                intersection.push([x_min.toFixed(6), xY_min.toFixed(6)]);
                                intersection.push([x_max.toFixed(6), xY_max.toFixed(6)]);
                            }
                        }
                    }
                }
                else {
                    // get intersection coordinates
                    var x1 = bbox1[bbox1_key[i]].x;
                    var x2 = bbox1[bbox1_key[i + 1]].x;
                    var x3 = bbox2[bbox2_key[j]].x;
                    var x4 = bbox2[bbox2_key[j + 1]].x;
                    var y1 = bbox1[bbox1_key[i]].y;
                    var y2 = bbox1[bbox1_key[i + 1]].y;
                    var y3 = bbox2[bbox2_key[j]].y;
                    var y4 = bbox2[bbox2_key[j + 1]].y;

                    var den = (x2 - x1) * (y3 - y4) - (y2 - y1) * (x3 - x4);
                    var t = ((x3 - x1) * (y3 - y4) - (y3 - y1) * (x3 - x4)) / den;
                    var u = ((x3 - x1) * (y1 - y2) - (y3 - y1) * (x1 - x2)) / den;
                    if ((t >= 0) && (t <= 1) && (u >= 0) && (u <= 1)) { // intersect happens if this condition is fulfilled
                        var ix = x1 + t * (x2 - x1);
                        var iy = y1 + t * (y2 - y1);
                        intersection.push([ix.toFixed(6), iy.toFixed(6)]);
                    }
                }
            }
        }
        // get clipped coordinates
        var specialcase1 = true;
        var specialcase2 = false;
        var temp1 = bbox1[bbox1_key[0]];
        var temp2 = bbox2[bbox2_key[0]];
        for (let i = 1; i < bbox1_key.length - 1; i++) { // special case
            if (temp1 !== bbox1[bbox1_key[i]]) {
                specialcase1 = false;
                break;
            }
        }
        for (let i = 1; i < bbox2_key.length - 1; i++) {
            if (temp2 !== bbox2[bbox2_key[i]]) {
                specialcase2 = false;
                break;
            }
        }
        if (!(specialcase2)) {
            for (let i = 0; i < bbox1_key.length - 1; i++) {
                var sign_check = [];
                for (let j = 0; j < bbox2_key.length - 1; j++) {
                    var x1 = bbox2[bbox2_key[j]].x
                    var x2 = bbox2[bbox2_key[j + 1]].x
                    var y1 = bbox2[bbox2_key[j]].y
                    var y2 = bbox2[bbox2_key[j + 1]].y
                    var cross_prod = (bbox1[bbox1_key[i]].x - x1) * (y2 - y1) - (bbox1[bbox1_key[i]].y - y1) * (x2 - x1);
                    var sign = (cross_prod >= 0) ? true : false;
                    sign_check.push(sign);
                }
                if (sign_check.length === 4 && (sign_check.every((val, index) => val === [true, true, true, true][index]) ||
                    sign_check.every((val, index) => val === [false, false, false, false][index]))) { // similar signs indicate the coordinate is within another bbox
                    intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                    //intersection.push([bbox1[bbox1_key[i]].x, bbox1[bbox1_key[i]].y]);
                }
            }
        }
        if (!(specialcase1)) {
            for (let i = 0; i < bbox2_key.length - 1; i++) { // similar purpose but for second bbox
                var sign_check = [];
                for (let j = 0; j < bbox1_key.length - 1; j++) {
                    var x1 = bbox1[bbox1_key[j]].x
                    var x2 = bbox1[bbox1_key[j + 1]].x
                    var y1 = bbox1[bbox1_key[j]].y
                    var y2 = bbox1[bbox1_key[j + 1]].y
                    var cross_prod = (bbox2[bbox2_key[i]].x - x1) * (y2 - y1) - (bbox2[bbox2_key[i]].y - y1) * (x2 - x1);
                    var sign = (cross_prod >= 0) ? true : false;
                    sign_check.push(sign);
                }
                if (sign_check.length === 4 && (sign_check.every((val, index) => val === [true, true, true, true][index]) ||
                    sign_check.every((val, index) => val === [false, false, false, false][index]))) {
                    intersection.push([bbox2[bbox2_key[i]].x.toFixed(6), bbox2[bbox2_key[i]].y.toFixed(6)]);
                    //intersection.push([bbox2[bbox2_key[i]].x, bbox2[bbox2_key[i]].y]);
                }
            }
        }
        intersection = intersection.filter((item, index, self) =>
            index === self.findIndex((t) => JSON.stringify(t) === JSON.stringify(item))
        );
        // sort intersection points
        if (intersection.length > 2) {
            var intersection_angle = [];
            var intersection_distance = [];

            let minIndex = intersection.reduce((minIdx, currentValue, currentIndex, array) => {
                if (currentValue[0] < array[minIdx][0]) {
                    return currentIndex;
                } else if (currentValue[0] === array[minIdx][0]) {
                    return currentValue[1] < array[minIdx][1] ? currentIndex : minIdx;
                } else {
                    return minIdx;
                }
            }, 0);
            var temp_intersection = intersection.filter((_, index) => index !== minIndex);
            for (let i = 0; i < temp_intersection.length; i++) {
                intersection_angle.push(Math.atan2(temp_intersection[i][1] - intersection[minIndex][1], temp_intersection[i][0] - intersection[minIndex][0]));
                intersection_distance.push((temp_intersection[i][1] - intersection[minIndex][1]) ** 2 + (temp_intersection[i][0] - intersection[minIndex][0]) ** 2);
            }
            let combined = temp_intersection.map((point, index) => ({ // sort intersect coordinates based on angle and then distance
                point: point,
                angle: intersection_angle[index],
                distance: intersection_distance[index]
            }));
            combined.sort((a, b) => a.angle - b.angle || a.distance - b.distance);
            let sorted_intersection = combined.map(item => item.point);
            sorted_intersection.unshift(intersection[minIndex]);
            sorted_intersection.push(intersection[minIndex]);
            // shoelace algorithm to calculate the intersect area
            var area = 0;
            for (let i = 0; i < sorted_intersection.length - 1; i++) {
                area += ((sorted_intersection[i][0] * sorted_intersection[i + 1][1])
                    - (sorted_intersection[i + 1][0] * sorted_intersection[i][1]))
            }
            return area;
        }
        return 0;
    }
    function calculateIOU(bbox1, bbox2, intersectArea) {
        if (intersectArea !== 0) {
            var lot_area1 = (bbox1["vertex1"].x * bbox1["vertex2"].y) + (bbox1["vertex2"].x * bbox1["vertex3"].y)
                + (bbox1["vertex3"].x * bbox1["vertex4"].y) + (bbox1["vertex4"].x * bbox1["vertex_0"].y)
                - ((bbox1["vertex2"].x * bbox1["vertex1"].y) + (bbox1["vertex3"].x * bbox1["vertex2"].y)
                    + (bbox1["vertex4"].x * bbox1["vertex3"].y) + (bbox1["vertex_0"].x * bbox1["vertex4"].y));

            var lot_area2 = (bbox2["vertex1"].x * bbox2["vertex2"].y) + (bbox2["vertex2"].x * bbox2["vertex3"].y)
                + (bbox2["vertex3"].x * bbox2["vertex4"].y) + (bbox2["vertex4"].x * bbox2["vertex_0"].y)
                - ((bbox2["vertex2"].x * bbox2["vertex1"].y) + (bbox2["vertex3"].x * bbox2["vertex2"].y)
                    + (bbox2["vertex4"].x * bbox2["vertex3"].y) + (bbox2["vertex_0"].x * bbox2["vertex4"].y));
            return intersectArea / (lot_area1 + lot_area2 - intersectArea)
        }
        return 0;
    }
    function calculateIOR(bbox1, intersectArea) {
        if (intersectArea !== 0) {
            var lot_area = (bbox1["vertex1"].x * bbox1["vertex2"].y) + (bbox1["vertex2"].x * bbox1["vertex3"].y)
                + (bbox1["vertex3"].x * bbox1["vertex4"].y) + (bbox1["vertex4"].x * bbox1["vertex_0"].y)
                - ((bbox1["vertex2"].x * bbox1["vertex1"].y) + (bbox1["vertex3"].x * bbox1["vertex2"].y)
                    + (bbox1["vertex4"].x * bbox1["vertex3"].y) + (bbox1["vertex_0"].x * bbox1["vertex4"].y));
            return intersectArea / lot_area;
        }
        return 0;
    }

    var occupied = [];
    var current_index = -1;
    // initialize lots to no object
    for (let key1 in payload["lots"]) {
        if (payload["lots"].hasOwnProperty(key1)) {
            occupied.push({ "occupied": 0 });
        }
    }
    for (let key1 in payload["objects"]) {
        current_index++;
        if (payload["objects"].hasOwnProperty(key1)) { // find all lots within the object centroid distance threshold
            var objectCentroid = getBboxCentroid(payload["objects"][key1]);
            var potential_region = [];
            for (let key2 in payload["lots"]) {
                if (payload["lots"].hasOwnProperty(key2)) {
                    var regionCentroid = getBboxCentroid(payload["lots"][key2]);
                    var euclideanDistance = calculateEuclideanDistance(objectCentroid, regionCentroid);
                    potential_region.push(euclideanDistance);
                }
            }
            // calculate IOU or IOR
            if (object_counter) {
                for (let i = 0; i < potential_region.length; i++) {
                    if (potential_region[i] < distance_threshold) {
                        var intersectRegion = calculateIntersectArea(payload["objects"][key1], payload["lots"][i]);
                        var IOR = 0;
                        if (intersection_type == "region") {
                            IOR = calculateIOR(payload["lots"][i], intersectRegion)
                        }
                        else if (intersection_type == "object") {
                            IOR = calculateIOR(payload["objects"][key1], intersectRegion)
                        }
                        if (IOR > intersection_threshold) {
                            occupied[i]["occupied"] += 1;
                            var keyData = Object.keys(payload["objects_extra"][current_index]);
                            var numberData = keyData.length;
                            for (let j = 0; j < numberData; j++) {
                                if (!occupied[i]["extra"]) {
                                    occupied[i]["extra"] = {};
                                }
                                if (!occupied[i]["extra"][keyData[j]]) {
                                    occupied[i]["extra"][keyData[j]] = [];
                                }
                                occupied[i]["extra"][keyData[j]].push(payload["objects_extra"][current_index][keyData[j]]);
                            }
                        }
                    }
                }
            }
            else { // consider the best lot only
                var best_region_index = potential_region.reduce((minIndex, currentValue, currentIndex, array) =>
                    currentValue < array[minIndex] ? currentIndex : minIndex, 0);
                if (potential_region[best_region_index] < distance_threshold) {
                    var intersectRegion = calculateIntersectArea(payload["objects"][key1], payload["lots"][best_region_index]);
                    var IOR = 0;
                    if (intersection_type == "region") {
                        IOR = calculateIOR(payload["lots"][best_region_index], intersectRegion)
                    }
                    else if (intersection_type == "object") {
                        IOR = calculateIOR(payload["objects"][key1], intersectRegion)
                    }
                    if (IOR > intersection_threshold) {
                        occupied[best_region_index]["occupied"] += 1;
                        var keyData = Object.keys(payload["objects_extra"][current_index]);
                        var numberData = keyData.length;
                        for (let i = 0; i < numberData; i++) {
                            if (!occupied[best_region_index]["extra"]) {
                                occupied[best_region_index]["extra"] = {};
                            }
                            if (!occupied[best_region_index]["extra"][keyData[i]]) {
                                occupied[best_region_index]["extra"][keyData[i]] = [];
                            }
                            occupied[best_region_index]["extra"][keyData[i]].push(payload["objects_extra"][current_index][keyData[i]]);
                        }
                    }
                }
            }
        }
    }
    return occupied;
}

// turn into correct format
function postprocess_output(occupied) {
    var results = {};

    for (let i = 0; i < occupied.length; i++) {
        results = initializeResults(results, occupied, i);
        previous_data = initializePreviousData(results, i);
        if (results["Region " + (i + 1)].hasOwnProperty("id") || previous_data["Region " + (i + 1)].hasOwnProperty("id")) {
            results = handleObjectWithID(results, i);
        }
        else if (results["Region " + (i + 1)]["occupied"] > 0) {
            results = handleOccupiedRegion(results, i);
        }
        else if (previous_data["Region " + (i + 1)]["entry_time"]) {
            results = handleNotOccupiedRegion(results, i);
        }
    }
    return results;
}

function initializeResults(results, occupied, i) {
    const regionKey = "Region " + (i + 1);

    if (!results[regionKey]) {
        results[regionKey] = {};
    }
    results[regionKey]["video_id"] = video_id;
    results[regionKey]["region_id"] = (region_id != -1) ? region_id : i + 1;

    if (occupied[i].hasOwnProperty("occupied")) {
        results[regionKey]["occupied"] = occupied[i].occupied;
    }

    if (occupied[i].hasOwnProperty("extra")) {
        for (let key in occupied[i].extra) {
            results[regionKey][key] = occupied[i].extra[key];
        }
    }
    if (results[regionKey].hasOwnProperty("id") || (previous_data && previous_data.hasOwnProperty(regionKey) && previous_data[regionKey].hasOwnProperty("id"))) {
        results[regionKey]["actual_entry_time"] = [];
        results[regionKey]["actual_dwell_time"] = [];
        results[regionKey]["entry_time"] = [];
        results[regionKey]["dwell_time"] = [];
        results[regionKey]["exit_time"] = [];
    }
    else {
        results["Region " + (i + 1)]["actual_entry_time"] = "";
        results["Region " + (i + 1)]["actual_dwell_time"] = "";
        results["Region " + (i + 1)]["entry_time"] = "";
        results["Region " + (i + 1)]["dwell_time"] = "";
        results["Region " + (i + 1)]["exit_time"] = "";
        results["Region " + (i + 1)]["occupancy_check"] = 0;
    }
    return results;
}

function initializePreviousData(results, i) {
    const regionKey = "Region " + (i + 1);

    // Ensure previous_data is set up for this region.
    if (!previous_data) {
        previous_data = {};
    }
    if (results[regionKey].hasOwnProperty("id")) {
        if (!previous_data[regionKey]) {
            previous_data[regionKey] = {};
            previous_data[regionKey]["id"] = [];
            previous_data[regionKey]["actual_entry_time"] = [];
            previous_data[regionKey]["actual_dwell_time"] = [];
            previous_data[regionKey]["entry_time"] = [];
            previous_data[regionKey]["dwell_time"] = [];
            previous_data[regionKey]["exit_time"] = [];
        }
    }
    else {
        if (!previous_data.hasOwnProperty(regionKey)) {
            previous_data[regionKey] = results[regionKey];
            previous_data[regionKey]["actual_entry_time"] = "";
            previous_data[regionKey]["actual_dwell_time"] = "";
            previous_data[regionKey]["entry_time"] = "";
            previous_data[regionKey]["dwell_time"] = "";
            previous_data[regionKey]["exit_time"] = "";
            previous_data[regionKey]["occupancy_check"] = 0;
        }
    }

    return previous_data;
}

function handleObjectWithID(results, i) {
    const regionKey = "Region " + (i + 1);
    const resultIds = results[regionKey]["id"];
    const previousDataIds = previous_data[regionKey]["id"];

    if (!resultIds && !previousDataIds) {
        return results;
    }

    if (resultIds) {
        for (let j = 0; j < resultIds.length; j++) {
            // Check existing id data
            let prevIndex = -1;

            if (previous_data[regionKey]["id"] && previous_data[regionKey]["id"].length > 0) {
                prevIndex = previous_data[regionKey]["id"].indexOf(resultIds[j]);
            }

            if (prevIndex === -1) {
                results[regionKey]["actual_entry_time"].push(msg.nodered_timestamp);
                results[regionKey]["entry_time"].push(msg.object_timestamp);
                results[regionKey]["actual_dwell_time"].push("00:00:00");
                results[regionKey]["dwell_time"].push(0);
                results[regionKey]["exit_time"].push("");
            } else {
                results[regionKey]["actual_entry_time"].push(previous_data[regionKey]["actual_entry_time"][prevIndex]);
                results[regionKey]["entry_time"].push(previous_data[regionKey]["entry_time"][prevIndex]);

                // Calculate dwell time.
                const dwell_timestamp = (msg.object_timestamp - results[regionKey]["entry_time"][j]);
                const dwell_hours = Math.floor(dwell_timestamp / 3600).toString().padStart(2, "0");
                const dwell_mins = Math.floor((dwell_timestamp / 60) % 60).toString().padStart(2, "0");
                const dwell_seconds = Math.floor(dwell_timestamp % 60).toString().padStart(2, "0");
                results[regionKey]["dwell_time"].push(dwell_timestamp);
                results[regionKey]["actual_dwell_time"].push(dwell_hours + ":" + dwell_mins + ":" + dwell_seconds);
                results[regionKey]["exit_time"].push("");
            }
        }
    }

    if (previousDataIds) {
        for (let j = 0; j < previousDataIds.length; j++) {
            let prevIndex = -1;

            if (!results[regionKey]["id"]) {
                results[regionKey] = previous_data[regionKey];
                results[regionKey]["occupied"] = 0;
                break;
            }
            else {
                if (!results[regionKey]["dwell_time"].some(val => val >= stop_duration)) {
                    results[regionKey]["occupied"] = 0;
                }

                prevIndex = results[regionKey]["id"].indexOf(previousDataIds[j]);
                if (prevIndex === -1) {
                    let keyList = Object.keys(previous_data[regionKey]);
                    for (let k = 0; k < keyList.length; k++) {
                        if (keyList[k] == "video_id" || keyList[k] == "region_id" || keyList[k] == "occupied") {
                            continue;
                        }
                        results[regionKey][keyList[k]].push(previous_data[regionKey][keyList[k]][j]);
                    }
                }
            }
        }
    }
    return results;
}

function handleOccupiedRegion(results, i) {
    const regionKey = "Region " + (i + 1);
    if (previous_data[regionKey]["entry_time"] == "") {
        results[regionKey]["actual_entry_time"] = msg.nodered_timestamp;
        results[regionKey]["entry_time"] = msg.object_timestamp;
    }
    else {
        if (previous_data[regionKey]["occupancy_check"] >= occupancy_check_duration) {
            results[regionKey]["actual_dwell_time"] = "00:00:00";
            results[regionKey]["dwell_time"] = 0;
            results[regionKey]["actual_entry_time"] = msg.nodered_timestamp;
            results[regionKey]["entry_time"] = msg.object_timestamp;
        }
        else {
            results[regionKey]["actual_entry_time"] = previous_data[regionKey]["actual_entry_time"];
            results[regionKey]["entry_time"] = previous_data[regionKey]["entry_time"];
        }

        var dwell_timestamp = msg.object_timestamp - results[regionKey]["entry_time"];
        var dwell_hours = Math.floor(dwell_timestamp / 3600).toString();
        dwell_hours = dwell_hours.toString().length == 1 ? dwell_hours.toString().padStart(2, "0") : dwell_hours.toString();
        var dwell_mins = Math.floor((dwell_timestamp / 60) % 60).toString().padStart(2, "0");
        var dwell_seconds = Math.floor(dwell_timestamp % 60).toString().padStart(2, "0");

        results[regionKey]["dwell_time"] = dwell_timestamp;
        results[regionKey]["actual_dwell_time"] = dwell_hours + ":" + dwell_mins + ":" + dwell_seconds;
        results[regionKey]["occupancy_check"] = 0;
    }

    return results;
}

function handleNotOccupiedRegion(results, i) {
    const regionKey = "Region " + (i + 1);

    results[regionKey]["actual_entry_time"] = previous_data[regionKey]["actual_entry_time"];
    results[regionKey]["entry_time"] = previous_data[regionKey]["entry_time"];
    results[regionKey]["actual_dwell_time"] = previous_data[regionKey]["actual_dwell_time"];
    results[regionKey]["dwell_time"] = previous_data[regionKey]["dwell_time"];
    results[regionKey]["occupancy_check"] = previous_data[regionKey]["occupancy_check"] + 1;

    if (previous_data[regionKey]["exit_time"]) {
        results[regionKey]["exit_time"] = previous_data[regionKey]["exit_time"];
    }
    else {
        results[regionKey]["exit_time"] = msg.nodered_timestamp;
    }
    return results;
}

function main(payload, distance_threshold = 100, intersection_type = "region", intersection_threshold = 0.5, object_counter = false) {
    var results = preprocess_bboxes(payload);
    var occupied = euclidean_iou_function(results, distance_threshold, intersection_type, intersection_threshold, object_counter);
    var out = postprocess_output(occupied);
    flow.set(previous_data_name, out);
    return out;
}

// user-defined parameters
var distance_threshold = flow.get("distance_threshold");
var intersection_type = flow.get("intersection_type");
var intersection_threshold = flow.get("intersection_threshold");
var object_counter = flow.get("object_counter");

msg.payload = main(payload, distance_threshold, intersection_type, intersection_threshold, object_counter);
flow.set(detection_name, msg);
return msg;

let region_name = "predefined_regions_2";
let detection_name = "object_detection_2";
let previous_data_name = "previous_data_2";
let occupancy_check_duration = 60;
let video_id = 2;
let region_id = -1; //Recommend to set this only when there is 1 predefined region in the video

var payload = msg.payload;
var previous_data = flow.get(previous_data_name);
var stop_duration = flow.get("stop_duration");

// preparing bboxes
function preprocess_bboxes(payload) {
    function getObjectData(object_data) {
        var current_object_data = {};
        if (object_data.hasOwnProperty("type")) {
            current_object_data["type"] = object_data["type"];
        }
        if (object_data.hasOwnProperty("color")) {
            current_object_data["color"] = object_data["color"];
        }
        if (object_data.hasOwnProperty("license_plate")) {
            current_object_data["license_plate"] = object_data["license_plate"];
        }
        if (object_data.hasOwnProperty("id")) {
            current_object_data["id"] = object_data["id"];
        }
        if (object_data.hasOwnProperty("roi_type")) {
            current_object_data["roi_type"] = object_data["roi_type"];
        }
        return current_object_data;
    }
    function getLotBbox(lot_data) {
        return { "vertex1": lot_data.vertex1, "vertex2": lot_data.vertex2, "vertex3": lot_data.vertex3, "vertex4": lot_data.vertex4, "vertex_0": lot_data.vertex1 };
    }

    var results = {};
    var objects = [];
    var objects_extra = [];
    var lots = [];
    // get all objects bboxes
    for (let key in payload) {
        if (payload.hasOwnProperty(key)) {
            var objectData = getObjectData(payload[key]);
            if (objectData !== null) {
                objects_extra.push(
                    objectData
                );
                objects.push(
                    payload[key]["bbox"]
                );
            }
        }
    }
    // get all lots bboxes
    var predefined_regions = flow.get(region_name);
    var keys = Object.keys(predefined_regions);

    for (let i = 0; i < keys.length; i++) {
        if (predefined_regions.hasOwnProperty(keys[i]) && keys[i] != "_msgid") {
            lots.push(
                getLotBbox(predefined_regions[keys[i]])
            );
        }
    }

    results["objects"] = objects;
    results["objects_extra"] = objects_extra;
    results["lots"] = lots;
    return results;
}

// euclidean distance and iou check
function euclidean_iou_function(payload, distance_threshold = 100, intersection_type = "region", intersection_threshold = 0.5, object_counter = false) {
    function getBboxCentroid(bbox) {
        var centroid_x = (bbox["vertex1"].x + bbox["vertex2"].x + bbox["vertex3"].x + bbox["vertex4"].x) / 4;
        var centroid_y = (bbox["vertex1"].y + bbox["vertex2"].y + bbox["vertex3"].y + bbox["vertex4"].y) / 4;
        return [centroid_x, centroid_y];
    }
    function calculateEuclideanDistance(centroid1, centroid2) {
        return Math.sqrt((centroid2[0] - centroid1[0]) ** 2 + (centroid2[1] - centroid1[1]) ** 2);
    }
    function calculateIntersectArea(bbox1, bbox2) { // objects, lots
        var bbox1_key = Object.keys(bbox1)
        var bbox2_key = Object.keys(bbox2)
        var intersection = [];

        for (let i = 0; i < bbox1_key.length - 1; i++) {
            for (let j = 0; j < bbox2_key.length - 1; j++) {
                var A1 = bbox1[bbox1_key[i + 1]].y - bbox1[bbox1_key[i]].y; // Ax + By + C = 0
                var B1 = bbox1[bbox1_key[i]].x - bbox1[bbox1_key[i + 1]].x;
                var C1 = - A1 * bbox1[bbox1_key[i]].x - B1 * bbox1[bbox1_key[i]].y;

                var A2 = bbox2[bbox2_key[j + 1]].y - bbox2[bbox2_key[j]].y;
                var B2 = bbox2[bbox2_key[j]].x - bbox2[bbox2_key[j + 1]].x;
                var C2 = - A2 * bbox2[bbox2_key[j]].x - B2 * bbox2[bbox2_key[j]].y;

                // parallel lines case
                if (A1 * B2 == A2 * B1) {
                    // coincide case
                    if ((A1 * C2 == A2 * C1) && (B1 * C2 == B2 * C1)) {
                        if (B1 == 0 && A1 == 0) { // one point bbox case
                            intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                        }
                        else if (B2 == 0 && A2 == 0) { // one point bbox case
                            intersection.push([bbox2[bbox2_key[i]].x.toFixed(6), bbox2[bbox2_key[i]].y.toFixed(6)]);
                        }
                        else if (B1 == 0) { // vertical lines case
                            var y_min1 = (bbox1[bbox1_key[i]].y < bbox1[bbox1_key[i + 1]].y) ? bbox1[bbox1_key[i]].y : bbox1[bbox1_key[i + 1]].y;
                            var y_max1 = (bbox1[bbox1_key[i]].y < bbox1[bbox1_key[i + 1]].y) ? bbox1[bbox1_key[i + 1]].y : bbox1[bbox1_key[i]].y;
                            var y_min2 = (bbox2[bbox2_key[i]].y < bbox2[bbox2_key[i + 1]].y) ? bbox2[bbox2_key[i]].y : bbox2[bbox2_key[i + 1]].y;
                            var y_max2 = (bbox2[bbox2_key[i]].y < bbox2[bbox2_key[i + 1]].y) ? bbox2[bbox2_key[i + 1]].y : bbox2[bbox2_key[i]].y;
                            if ((y_min1 < y_max2) && (y_min2 < y_max1)) {
                                var y_min = (y_min1 < y_min2) ? y_min2 : y_min1;
                                var y_max = (y_max1 < y_max2) ? y_max1 : y_max2;
                                intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), y_min.toFixed(6)]);
                                intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), y_max.toFixed(6)]);
                            }
                        }
                        else if (A1 == 0) { // horizontal lines case
                            var x_min1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i]].x : bbox1[bbox1_key[i + 1]].x;
                            var x_max1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i + 1]].x : bbox1[bbox1_key[i]].x;
                            var x_min2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i]].x : bbox2[bbox2_key[i + 1]].x;
                            var x_max2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i + 1]].x : bbox2[bbox2_key[i]].x;
                            if ((x_min1 < x_max2) && (x_min2 < x_max1)) {
                                var x_min = (x_min1 < x_min2) ? x_min2 : x_min1;
                                var x_max = (x_max1 < x_max2) ? x_max1 : x_max2;
                                intersection.push([x_min.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                                intersection.push([x_max.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                            }
                        }
                        else {
                            var x_min1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i]].x : bbox1[bbox1_key[i + 1]].x;
                            var x_max1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i + 1]].x : bbox1[bbox1_key[i]].x;
                            var x_min2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i]].x : bbox2[bbox2_key[i + 1]].x;
                            var x_max2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i + 1]].x : bbox2[bbox2_key[i]].x;
                            if ((x_min1 < x_max2) && (x_min2 < x_max1) && (y_min1 < y_max2) && (y_min2 < y_max1)) {
                                var x_min = (x_min1 < x_min2) ? x_min2 : x_min1;
                                var x_max = (x_max1 < x_max2) ? x_max1 : x_max2;
                                var xY_min = (-A1 * x_min - C1) / B1
                                var xY_max = (-A1 * x_max - C1) / B1
                                intersection.push([x_min.toFixed(6), xY_min.toFixed(6)]);
                                intersection.push([x_max.toFixed(6), xY_max.toFixed(6)]);
                            }
                        }
                    }
                }
                else {
                    // get intersection coordinates
                    var x1 = bbox1[bbox1_key[i]].x;
                    var x2 = bbox1[bbox1_key[i + 1]].x;
                    var x3 = bbox2[bbox2_key[j]].x;
                    var x4 = bbox2[bbox2_key[j + 1]].x;
                    var y1 = bbox1[bbox1_key[i]].y;
                    var y2 = bbox1[bbox1_key[i + 1]].y;
                    var y3 = bbox2[bbox2_key[j]].y;
                    var y4 = bbox2[bbox2_key[j + 1]].y;

                    var den = (x2 - x1) * (y3 - y4) - (y2 - y1) * (x3 - x4);
                    var t = ((x3 - x1) * (y3 - y4) - (y3 - y1) * (x3 - x4)) / den;
                    var u = ((x3 - x1) * (y1 - y2) - (y3 - y1) * (x1 - x2)) / den;
                    if ((t >= 0) && (t <= 1) && (u >= 0) && (u <= 1)) { // intersect happens if this condition is fulfilled
                        var ix = x1 + t * (x2 - x1);
                        var iy = y1 + t * (y2 - y1);
                        intersection.push([ix.toFixed(6), iy.toFixed(6)]);
                    }
                }
            }
        }
        // get clipped coordinates
        var specialcase1 = true;
        var specialcase2 = false;
        var temp1 = bbox1[bbox1_key[0]];
        var temp2 = bbox2[bbox2_key[0]];
        for (let i = 1; i < bbox1_key.length - 1; i++) { // special case
            if (temp1 !== bbox1[bbox1_key[i]]) {
                specialcase1 = false;
                break;
            }
        }
        for (let i = 1; i < bbox2_key.length - 1; i++) {
            if (temp2 !== bbox2[bbox2_key[i]]) {
                specialcase2 = false;
                break;
            }
        }
        if (!(specialcase2)) {
            for (let i = 0; i < bbox1_key.length - 1; i++) {
                var sign_check = [];
                for (let j = 0; j < bbox2_key.length - 1; j++) {
                    var x1 = bbox2[bbox2_key[j]].x
                    var x2 = bbox2[bbox2_key[j + 1]].x
                    var y1 = bbox2[bbox2_key[j]].y
                    var y2 = bbox2[bbox2_key[j + 1]].y
                    var cross_prod = (bbox1[bbox1_key[i]].x - x1) * (y2 - y1) - (bbox1[bbox1_key[i]].y - y1) * (x2 - x1);
                    var sign = (cross_prod >= 0) ? true : false;
                    sign_check.push(sign);
                }
                if (sign_check.length === 4 && (sign_check.every((val, index) => val === [true, true, true, true][index]) ||
                    sign_check.every((val, index) => val === [false, false, false, false][index]))) { // similar signs indicate the coordinate is within another bbox
                    intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                    //intersection.push([bbox1[bbox1_key[i]].x, bbox1[bbox1_key[i]].y]);
                }
            }
        }
        if (!(specialcase1)) {
            for (let i = 0; i < bbox2_key.length - 1; i++) { // similar purpose but for second bbox
                var sign_check = [];
                for (let j = 0; j < bbox1_key.length - 1; j++) {
                    var x1 = bbox1[bbox1_key[j]].x
                    var x2 = bbox1[bbox1_key[j + 1]].x
                    var y1 = bbox1[bbox1_key[j]].y
                    var y2 = bbox1[bbox1_key[j + 1]].y
                    var cross_prod = (bbox2[bbox2_key[i]].x - x1) * (y2 - y1) - (bbox2[bbox2_key[i]].y - y1) * (x2 - x1);
                    var sign = (cross_prod >= 0) ? true : false;
                    sign_check.push(sign);
                }
                if (sign_check.length === 4 && (sign_check.every((val, index) => val === [true, true, true, true][index]) ||
                    sign_check.every((val, index) => val === [false, false, false, false][index]))) {
                    intersection.push([bbox2[bbox2_key[i]].x.toFixed(6), bbox2[bbox2_key[i]].y.toFixed(6)]);
                    //intersection.push([bbox2[bbox2_key[i]].x, bbox2[bbox2_key[i]].y]);
                }
            }
        }
        intersection = intersection.filter((item, index, self) =>
            index === self.findIndex((t) => JSON.stringify(t) === JSON.stringify(item))
        );
        // sort intersection points
        if (intersection.length > 2) {
            var intersection_angle = [];
            var intersection_distance = [];

            let minIndex = intersection.reduce((minIdx, currentValue, currentIndex, array) => {
                if (currentValue[0] < array[minIdx][0]) {
                    return currentIndex;
                } else if (currentValue[0] === array[minIdx][0]) {
                    return currentValue[1] < array[minIdx][1] ? currentIndex : minIdx;
                } else {
                    return minIdx;
                }
            }, 0);
            var temp_intersection = intersection.filter((_, index) => index !== minIndex);
            for (let i = 0; i < temp_intersection.length; i++) {
                intersection_angle.push(Math.atan2(temp_intersection[i][1] - intersection[minIndex][1], temp_intersection[i][0] - intersection[minIndex][0]));
                intersection_distance.push((temp_intersection[i][1] - intersection[minIndex][1]) ** 2 + (temp_intersection[i][0] - intersection[minIndex][0]) ** 2);
            }
            let combined = temp_intersection.map((point, index) => ({ // sort intersect coordinates based on angle and then distance
                point: point,
                angle: intersection_angle[index],
                distance: intersection_distance[index]
            }));
            combined.sort((a, b) => a.angle - b.angle || a.distance - b.distance);
            let sorted_intersection = combined.map(item => item.point);
            sorted_intersection.unshift(intersection[minIndex]);
            sorted_intersection.push(intersection[minIndex]);
            // shoelace algorithm to calculate the intersect area
            var area = 0;
            for (let i = 0; i < sorted_intersection.length - 1; i++) {
                area += ((sorted_intersection[i][0] * sorted_intersection[i + 1][1])
                    - (sorted_intersection[i + 1][0] * sorted_intersection[i][1]))
            }
            return area;
        }
        return 0;
    }
    function calculateIOU(bbox1, bbox2, intersectArea) {
        if (intersectArea !== 0) {
            var lot_area1 = (bbox1["vertex1"].x * bbox1["vertex2"].y) + (bbox1["vertex2"].x * bbox1["vertex3"].y)
                + (bbox1["vertex3"].x * bbox1["vertex4"].y) + (bbox1["vertex4"].x * bbox1["vertex_0"].y)
                - ((bbox1["vertex2"].x * bbox1["vertex1"].y) + (bbox1["vertex3"].x * bbox1["vertex2"].y)
                    + (bbox1["vertex4"].x * bbox1["vertex3"].y) + (bbox1["vertex_0"].x * bbox1["vertex4"].y));

            var lot_area2 = (bbox2["vertex1"].x * bbox2["vertex2"].y) + (bbox2["vertex2"].x * bbox2["vertex3"].y)
                + (bbox2["vertex3"].x * bbox2["vertex4"].y) + (bbox2["vertex4"].x * bbox2["vertex_0"].y)
                - ((bbox2["vertex2"].x * bbox2["vertex1"].y) + (bbox2["vertex3"].x * bbox2["vertex2"].y)
                    + (bbox2["vertex4"].x * bbox2["vertex3"].y) + (bbox2["vertex_0"].x * bbox2["vertex4"].y));
            return intersectArea / (lot_area1 + lot_area2 - intersectArea)
        }
        return 0;
    }
    function calculateIOR(bbox1, intersectArea) {
        if (intersectArea !== 0) {
            var lot_area = (bbox1["vertex1"].x * bbox1["vertex2"].y) + (bbox1["vertex2"].x * bbox1["vertex3"].y)
                + (bbox1["vertex3"].x * bbox1["vertex4"].y) + (bbox1["vertex4"].x * bbox1["vertex_0"].y)
                - ((bbox1["vertex2"].x * bbox1["vertex1"].y) + (bbox1["vertex3"].x * bbox1["vertex2"].y)
                    + (bbox1["vertex4"].x * bbox1["vertex3"].y) + (bbox1["vertex_0"].x * bbox1["vertex4"].y));
            return intersectArea / lot_area;
        }
        return 0;
    }

    var occupied = [];
    var current_index = -1;
    // initialize lots to no object
    for (let key1 in payload["lots"]) {
        if (payload["lots"].hasOwnProperty(key1)) {
            occupied.push({ "occupied": 0 });
        }
    }
    for (let key1 in payload["objects"]) {
        current_index++;
        if (payload["objects"].hasOwnProperty(key1)) { // find all lots within the object centroid distance threshold
            var objectCentroid = getBboxCentroid(payload["objects"][key1]);
            var potential_region = [];
            for (let key2 in payload["lots"]) {
                if (payload["lots"].hasOwnProperty(key2)) {
                    var regionCentroid = getBboxCentroid(payload["lots"][key2]);
                    var euclideanDistance = calculateEuclideanDistance(objectCentroid, regionCentroid);
                    potential_region.push(euclideanDistance);
                }
            }
            // calculate IOU or IOR
            if (object_counter) {
                for (let i = 0; i < potential_region.length; i++) {
                    if (potential_region[i] < distance_threshold) {
                        var intersectRegion = calculateIntersectArea(payload["objects"][key1], payload["lots"][i]);
                        var IOR = 0;
                        if (intersection_type == "region") {
                            IOR = calculateIOR(payload["lots"][i], intersectRegion)
                        }
                        else if (intersection_type == "object") {
                            IOR = calculateIOR(payload["objects"][key1], intersectRegion)
                        }
                        if (IOR > intersection_threshold) {
                            occupied[i]["occupied"] += 1;
                            var keyData = Object.keys(payload["objects_extra"][current_index]);
                            var numberData = keyData.length;
                            for (let j = 0; j < numberData; j++) {
                                if (!occupied[i]["extra"]) {
                                    occupied[i]["extra"] = {};
                                }
                                if (!occupied[i]["extra"][keyData[j]]) {
                                    occupied[i]["extra"][keyData[j]] = [];
                                }
                                occupied[i]["extra"][keyData[j]].push(payload["objects_extra"][current_index][keyData[j]]);
                            }
                        }
                    }
                }
            }
            else { // consider the best lot only
                var best_region_index = potential_region.reduce((minIndex, currentValue, currentIndex, array) =>
                    currentValue < array[minIndex] ? currentIndex : minIndex, 0);
                if (potential_region[best_region_index] < distance_threshold) {
                    var intersectRegion = calculateIntersectArea(payload["objects"][key1], payload["lots"][best_region_index]);
                    var IOR = 0;
                    if (intersection_type == "region") {
                        IOR = calculateIOR(payload["lots"][best_region_index], intersectRegion)
                    }
                    else if (intersection_type == "object") {
                        IOR = calculateIOR(payload["objects"][key1], intersectRegion)
                    }
                    if (IOR > intersection_threshold) {
                        occupied[best_region_index]["occupied"] += 1;
                        var keyData = Object.keys(payload["objects_extra"][current_index]);
                        var numberData = keyData.length;
                        for (let i = 0; i < numberData; i++) {
                            if (!occupied[best_region_index]["extra"]) {
                                occupied[best_region_index]["extra"] = {};
                            }
                            if (!occupied[best_region_index]["extra"][keyData[i]]) {
                                occupied[best_region_index]["extra"][keyData[i]] = [];
                            }
                            occupied[best_region_index]["extra"][keyData[i]].push(payload["objects_extra"][current_index][keyData[i]]);
                        }
                    }
                }
            }
        }
    }
    return occupied;
}

// turn into correct format
function postprocess_output(occupied) {
    var results = {};

    for (let i = 0; i < occupied.length; i++) {
        results = initializeResults(results, occupied, i);
        previous_data = initializePreviousData(results, i);
        if (results["Region " + (i + 1)].hasOwnProperty("id") || previous_data["Region " + (i + 1)].hasOwnProperty("id")) {
            results = handleObjectWithID(results, i);
        }
        else if (results["Region " + (i + 1)]["occupied"] > 0) {
            results = handleOccupiedRegion(results, i);
        }
        else if (previous_data["Region " + (i + 1)]["entry_time"]) {
            results = handleNotOccupiedRegion(results, i);
        }
    }
    return results;
}

function initializeResults(results, occupied, i) {
    const regionKey = "Region " + (i + 1);

    if (!results[regionKey]) {
        results[regionKey] = {};
    }
    results[regionKey]["video_id"] = video_id;
    results[regionKey]["region_id"] = (region_id != -1) ? region_id : i + 1;

    if (occupied[i].hasOwnProperty("occupied")) {
        results[regionKey]["occupied"] = occupied[i].occupied;
    }

    if (occupied[i].hasOwnProperty("extra")) {
        for (let key in occupied[i].extra) {
            results[regionKey][key] = occupied[i].extra[key];
        }
    }
    if (results[regionKey].hasOwnProperty("id") || (previous_data && previous_data.hasOwnProperty(regionKey) && previous_data[regionKey].hasOwnProperty("id"))) {
        results[regionKey]["actual_entry_time"] = [];
        results[regionKey]["actual_dwell_time"] = [];
        results[regionKey]["entry_time"] = [];
        results[regionKey]["dwell_time"] = [];
        results[regionKey]["exit_time"] = [];
    }
    else {
        results["Region " + (i + 1)]["actual_entry_time"] = "";
        results["Region " + (i + 1)]["actual_dwell_time"] = "";
        results["Region " + (i + 1)]["entry_time"] = "";
        results["Region " + (i + 1)]["dwell_time"] = "";
        results["Region " + (i + 1)]["exit_time"] = "";
        results["Region " + (i + 1)]["occupancy_check"] = 0;
    }
    return results;
}

function initializePreviousData(results, i) {
    const regionKey = "Region " + (i + 1);

    // Ensure previous_data is set up for this region.
    if (!previous_data) {
        previous_data = {};
    }
    if (results[regionKey].hasOwnProperty("id")) {
        if (!previous_data[regionKey]) {
            previous_data[regionKey] = {};
            previous_data[regionKey]["id"] = [];
            previous_data[regionKey]["actual_entry_time"] = [];
            previous_data[regionKey]["actual_dwell_time"] = [];
            previous_data[regionKey]["entry_time"] = [];
            previous_data[regionKey]["dwell_time"] = [];
            previous_data[regionKey]["exit_time"] = [];
        }
    }
    else {
        if (!previous_data.hasOwnProperty(regionKey)) {
            previous_data[regionKey] = results[regionKey];
            previous_data[regionKey]["actual_entry_time"] = "";
            previous_data[regionKey]["actual_dwell_time"] = "";
            previous_data[regionKey]["entry_time"] = "";
            previous_data[regionKey]["dwell_time"] = "";
            previous_data[regionKey]["exit_time"] = "";
            previous_data[regionKey]["occupancy_check"] = 0;
        }
    }

    return previous_data;
}

function handleObjectWithID(results, i) {
    const regionKey = "Region " + (i + 1);
    const resultIds = results[regionKey]["id"];
    const previousDataIds = previous_data[regionKey]["id"];

    if (!resultIds && !previousDataIds) {
        return results;
    }

    if (resultIds) {
        for (let j = 0; j < resultIds.length; j++) {
            // Check existing id data
            let prevIndex = -1;

            if (previous_data[regionKey]["id"] && previous_data[regionKey]["id"].length > 0) {
                prevIndex = previous_data[regionKey]["id"].indexOf(resultIds[j]);
            }

            if (prevIndex === -1) {
                results[regionKey]["actual_entry_time"].push(msg.nodered_timestamp);
                results[regionKey]["entry_time"].push(msg.object_timestamp);
                results[regionKey]["actual_dwell_time"].push("00:00:00");
                results[regionKey]["dwell_time"].push(0);
                results[regionKey]["exit_time"].push("");
            } else {
                results[regionKey]["actual_entry_time"].push(previous_data[regionKey]["actual_entry_time"][prevIndex]);
                results[regionKey]["entry_time"].push(previous_data[regionKey]["entry_time"][prevIndex]);

                // Calculate dwell time.
                const dwell_timestamp = (msg.object_timestamp - results[regionKey]["entry_time"][j]);
                const dwell_hours = Math.floor(dwell_timestamp / 3600).toString().padStart(2, "0");
                const dwell_mins = Math.floor((dwell_timestamp / 60) % 60).toString().padStart(2, "0");
                const dwell_seconds = Math.floor(dwell_timestamp % 60).toString().padStart(2, "0");
                results[regionKey]["dwell_time"].push(dwell_timestamp);
                results[regionKey]["actual_dwell_time"].push(dwell_hours + ":" + dwell_mins + ":" + dwell_seconds);
                results[regionKey]["exit_time"].push("");
            }
        }
    }

    if (previousDataIds) {
        for (let j = 0; j < previousDataIds.length; j++) {
            let prevIndex = -1;

            if (!results[regionKey]["id"]) {
                results[regionKey] = previous_data[regionKey];
                results[regionKey]["occupied"] = 0;
                break;
            }
            else {
                if (!results[regionKey]["dwell_time"].some(val => val >= stop_duration)) {
                    results[regionKey]["occupied"] = 0;
                }

                prevIndex = results[regionKey]["id"].indexOf(previousDataIds[j]);
                if (prevIndex === -1) {
                    let keyList = Object.keys(previous_data[regionKey]);
                    for (let k = 0; k < keyList.length; k++) {
                        if (keyList[k] == "video_id" || keyList[k] == "region_id" || keyList[k] == "occupied") {
                            continue;
                        }
                        results[regionKey][keyList[k]].push(previous_data[regionKey][keyList[k]][j]);
                    }
                }
            }
        }
    }
    return results;
}

function handleOccupiedRegion(results, i) {
    const regionKey = "Region " + (i + 1);
    if (previous_data[regionKey]["entry_time"] == "") {
        results[regionKey]["actual_entry_time"] = msg.nodered_timestamp;
        results[regionKey]["entry_time"] = msg.object_timestamp;
    }
    else {
        if (previous_data[regionKey]["occupancy_check"] >= occupancy_check_duration) {
            results[regionKey]["actual_dwell_time"] = "00:00:00";
            results[regionKey]["dwell_time"] = 0;
            results[regionKey]["actual_entry_time"] = msg.nodered_timestamp;
            results[regionKey]["entry_time"] = msg.object_timestamp;
        }
        else {
            results[regionKey]["actual_entry_time"] = previous_data[regionKey]["actual_entry_time"];
            results[regionKey]["entry_time"] = previous_data[regionKey]["entry_time"];
        }

        var dwell_timestamp = msg.object_timestamp - results[regionKey]["entry_time"];
        var dwell_hours = Math.floor(dwell_timestamp / 3600).toString();
        dwell_hours = dwell_hours.toString().length == 1 ? dwell_hours.toString().padStart(2, "0") : dwell_hours.toString();
        var dwell_mins = Math.floor((dwell_timestamp / 60) % 60).toString().padStart(2, "0");
        var dwell_seconds = Math.floor(dwell_timestamp % 60).toString().padStart(2, "0");

        results[regionKey]["dwell_time"] = dwell_timestamp;
        results[regionKey]["actual_dwell_time"] = dwell_hours + ":" + dwell_mins + ":" + dwell_seconds;
        results[regionKey]["occupancy_check"] = 0;
    }

    return results;
}

function handleNotOccupiedRegion(results, i) {
    const regionKey = "Region " + (i + 1);

    results[regionKey]["actual_entry_time"] = previous_data[regionKey]["actual_entry_time"];
    results[regionKey]["entry_time"] = previous_data[regionKey]["entry_time"];
    results[regionKey]["actual_dwell_time"] = previous_data[regionKey]["actual_dwell_time"];
    results[regionKey]["dwell_time"] = previous_data[regionKey]["dwell_time"];
    results[regionKey]["occupancy_check"] = previous_data[regionKey]["occupancy_check"] + 1;

    if (previous_data[regionKey]["exit_time"]) {
        results[regionKey]["exit_time"] = previous_data[regionKey]["exit_time"];
    }
    else {
        results[regionKey]["exit_time"] = msg.nodered_timestamp;
    }
    return results;
}

function main(payload, distance_threshold = 100, intersection_type = "region", intersection_threshold = 0.5, object_counter = false) {
    var results = preprocess_bboxes(payload);
    var occupied = euclidean_iou_function(results, distance_threshold, intersection_type, intersection_threshold, object_counter);
    var out = postprocess_output(occupied);
    flow.set(previous_data_name, out);
    return out;
}

// user-defined parameters
var distance_threshold = flow.get("distance_threshold");
var intersection_type = flow.get("intersection_type");
var intersection_threshold = flow.get("intersection_threshold");
var object_counter = flow.get("object_counter");

msg.payload = main(payload, distance_threshold, intersection_type, intersection_threshold, object_counter);
flow.set(detection_name, msg);
return msg;

let region_name = "predefined_regions_3";
let detection_name = "object_detection_3";
let previous_data_name = "previous_data_3";
let occupancy_check_duration = 60;
let video_id = 3;
let region_id = -1; //Recommend to set this only when there is 1 predefined region in the video

var payload = msg.payload;
var previous_data = flow.get(previous_data_name);
var stop_duration = flow.get("stop_duration");

// preparing bboxes
function preprocess_bboxes(payload) {
    function getObjectData(object_data) {
        var current_object_data = {};
        if (object_data.hasOwnProperty("type")) {
            current_object_data["type"] = object_data["type"];
        }
        if (object_data.hasOwnProperty("color")) {
            current_object_data["color"] = object_data["color"];
        }
        if (object_data.hasOwnProperty("license_plate")) {
            current_object_data["license_plate"] = object_data["license_plate"];
        }
        if (object_data.hasOwnProperty("id")) {
            current_object_data["id"] = object_data["id"];
        }
        if (object_data.hasOwnProperty("roi_type")) {
            current_object_data["roi_type"] = object_data["roi_type"];
        }
        return current_object_data;
    }
    function getLotBbox(lot_data) {
        return { "vertex1": lot_data.vertex1, "vertex2": lot_data.vertex2, "vertex3": lot_data.vertex3, "vertex4": lot_data.vertex4, "vertex_0": lot_data.vertex1 };
    }

    var results = {};
    var objects = [];
    var objects_extra = [];
    var lots = [];
    // get all objects bboxes
    for (let key in payload) {
        if (payload.hasOwnProperty(key)) {
            var objectData = getObjectData(payload[key]);
            if (objectData !== null) {
                objects_extra.push(
                    objectData
                );
                objects.push(
                    payload[key]["bbox"]
                );
            }
        }
    }
    // get all lots bboxes
    var predefined_regions = flow.get(region_name);
    var keys = Object.keys(predefined_regions);

    for (let i = 0; i < keys.length; i++) {
        if (predefined_regions.hasOwnProperty(keys[i]) && keys[i] != "_msgid") {
            lots.push(
                getLotBbox(predefined_regions[keys[i]])
            );
        }
    }

    results["objects"] = objects;
    results["objects_extra"] = objects_extra;
    results["lots"] = lots;
    return results;
}

// euclidean distance and iou check
function euclidean_iou_function(payload, distance_threshold = 100, intersection_type = "region", intersection_threshold = 0.5, object_counter = false) {
    function getBboxCentroid(bbox) {
        var centroid_x = (bbox["vertex1"].x + bbox["vertex2"].x + bbox["vertex3"].x + bbox["vertex4"].x) / 4;
        var centroid_y = (bbox["vertex1"].y + bbox["vertex2"].y + bbox["vertex3"].y + bbox["vertex4"].y) / 4;
        return [centroid_x, centroid_y];
    }
    function calculateEuclideanDistance(centroid1, centroid2) {
        return Math.sqrt((centroid2[0] - centroid1[0]) ** 2 + (centroid2[1] - centroid1[1]) ** 2);
    }
    function calculateIntersectArea(bbox1, bbox2) { // objects, lots
        var bbox1_key = Object.keys(bbox1)
        var bbox2_key = Object.keys(bbox2)
        var intersection = [];

        for (let i = 0; i < bbox1_key.length - 1; i++) {
            for (let j = 0; j < bbox2_key.length - 1; j++) {
                var A1 = bbox1[bbox1_key[i + 1]].y - bbox1[bbox1_key[i]].y; // Ax + By + C = 0
                var B1 = bbox1[bbox1_key[i]].x - bbox1[bbox1_key[i + 1]].x;
                var C1 = - A1 * bbox1[bbox1_key[i]].x - B1 * bbox1[bbox1_key[i]].y;

                var A2 = bbox2[bbox2_key[j + 1]].y - bbox2[bbox2_key[j]].y;
                var B2 = bbox2[bbox2_key[j]].x - bbox2[bbox2_key[j + 1]].x;
                var C2 = - A2 * bbox2[bbox2_key[j]].x - B2 * bbox2[bbox2_key[j]].y;

                // parallel lines case
                if (A1 * B2 == A2 * B1) {
                    // coincide case
                    if ((A1 * C2 == A2 * C1) && (B1 * C2 == B2 * C1)) {
                        if (B1 == 0 && A1 == 0) { // one point bbox case
                            intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                        }
                        else if (B2 == 0 && A2 == 0) { // one point bbox case
                            intersection.push([bbox2[bbox2_key[i]].x.toFixed(6), bbox2[bbox2_key[i]].y.toFixed(6)]);
                        }
                        else if (B1 == 0) { // vertical lines case
                            var y_min1 = (bbox1[bbox1_key[i]].y < bbox1[bbox1_key[i + 1]].y) ? bbox1[bbox1_key[i]].y : bbox1[bbox1_key[i + 1]].y;
                            var y_max1 = (bbox1[bbox1_key[i]].y < bbox1[bbox1_key[i + 1]].y) ? bbox1[bbox1_key[i + 1]].y : bbox1[bbox1_key[i]].y;
                            var y_min2 = (bbox2[bbox2_key[i]].y < bbox2[bbox2_key[i + 1]].y) ? bbox2[bbox2_key[i]].y : bbox2[bbox2_key[i + 1]].y;
                            var y_max2 = (bbox2[bbox2_key[i]].y < bbox2[bbox2_key[i + 1]].y) ? bbox2[bbox2_key[i + 1]].y : bbox2[bbox2_key[i]].y;
                            if ((y_min1 < y_max2) && (y_min2 < y_max1)) {
                                var y_min = (y_min1 < y_min2) ? y_min2 : y_min1;
                                var y_max = (y_max1 < y_max2) ? y_max1 : y_max2;
                                intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), y_min.toFixed(6)]);
                                intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), y_max.toFixed(6)]);
                            }
                        }
                        else if (A1 == 0) { // horizontal lines case
                            var x_min1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i]].x : bbox1[bbox1_key[i + 1]].x;
                            var x_max1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i + 1]].x : bbox1[bbox1_key[i]].x;
                            var x_min2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i]].x : bbox2[bbox2_key[i + 1]].x;
                            var x_max2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i + 1]].x : bbox2[bbox2_key[i]].x;
                            if ((x_min1 < x_max2) && (x_min2 < x_max1)) {
                                var x_min = (x_min1 < x_min2) ? x_min2 : x_min1;
                                var x_max = (x_max1 < x_max2) ? x_max1 : x_max2;
                                intersection.push([x_min.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                                intersection.push([x_max.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                            }
                        }
                        else {
                            var x_min1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i]].x : bbox1[bbox1_key[i + 1]].x;
                            var x_max1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i + 1]].x : bbox1[bbox1_key[i]].x;
                            var x_min2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i]].x : bbox2[bbox2_key[i + 1]].x;
                            var x_max2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i + 1]].x : bbox2[bbox2_key[i]].x;
                            if ((x_min1 < x_max2) && (x_min2 < x_max1) && (y_min1 < y_max2) && (y_min2 < y_max1)) {
                                var x_min = (x_min1 < x_min2) ? x_min2 : x_min1;
                                var x_max = (x_max1 < x_max2) ? x_max1 : x_max2;
                                var xY_min = (-A1 * x_min - C1) / B1
                                var xY_max = (-A1 * x_max - C1) / B1
                                intersection.push([x_min.toFixed(6), xY_min.toFixed(6)]);
                                intersection.push([x_max.toFixed(6), xY_max.toFixed(6)]);
                            }
                        }
                    }
                }
                else {
                    // get intersection coordinates
                    var x1 = bbox1[bbox1_key[i]].x;
                    var x2 = bbox1[bbox1_key[i + 1]].x;
                    var x3 = bbox2[bbox2_key[j]].x;
                    var x4 = bbox2[bbox2_key[j + 1]].x;
                    var y1 = bbox1[bbox1_key[i]].y;
                    var y2 = bbox1[bbox1_key[i + 1]].y;
                    var y3 = bbox2[bbox2_key[j]].y;
                    var y4 = bbox2[bbox2_key[j + 1]].y;

                    var den = (x2 - x1) * (y3 - y4) - (y2 - y1) * (x3 - x4);
                    var t = ((x3 - x1) * (y3 - y4) - (y3 - y1) * (x3 - x4)) / den;
                    var u = ((x3 - x1) * (y1 - y2) - (y3 - y1) * (x1 - x2)) / den;
                    if ((t >= 0) && (t <= 1) && (u >= 0) && (u <= 1)) { // intersect happens if this condition is fulfilled
                        var ix = x1 + t * (x2 - x1);
                        var iy = y1 + t * (y2 - y1);
                        intersection.push([ix.toFixed(6), iy.toFixed(6)]);
                    }
                }
            }
        }
        // get clipped coordinates
        var specialcase1 = true;
        var specialcase2 = false;
        var temp1 = bbox1[bbox1_key[0]];
        var temp2 = bbox2[bbox2_key[0]];
        for (let i = 1; i < bbox1_key.length - 1; i++) { // special case
            if (temp1 !== bbox1[bbox1_key[i]]) {
                specialcase1 = false;
                break;
            }
        }
        for (let i = 1; i < bbox2_key.length - 1; i++) {
            if (temp2 !== bbox2[bbox2_key[i]]) {
                specialcase2 = false;
                break;
            }
        }
        if (!(specialcase2)) {
            for (let i = 0; i < bbox1_key.length - 1; i++) {
                var sign_check = [];
                for (let j = 0; j < bbox2_key.length - 1; j++) {
                    var x1 = bbox2[bbox2_key[j]].x
                    var x2 = bbox2[bbox2_key[j + 1]].x
                    var y1 = bbox2[bbox2_key[j]].y
                    var y2 = bbox2[bbox2_key[j + 1]].y
                    var cross_prod = (bbox1[bbox1_key[i]].x - x1) * (y2 - y1) - (bbox1[bbox1_key[i]].y - y1) * (x2 - x1);
                    var sign = (cross_prod >= 0) ? true : false;
                    sign_check.push(sign);
                }
                if (sign_check.length === 4 && (sign_check.every((val, index) => val === [true, true, true, true][index]) ||
                    sign_check.every((val, index) => val === [false, false, false, false][index]))) { // similar signs indicate the coordinate is within another bbox
                    intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                    //intersection.push([bbox1[bbox1_key[i]].x, bbox1[bbox1_key[i]].y]);
                }
            }
        }
        if (!(specialcase1)) {
            for (let i = 0; i < bbox2_key.length - 1; i++) { // similar purpose but for second bbox
                var sign_check = [];
                for (let j = 0; j < bbox1_key.length - 1; j++) {
                    var x1 = bbox1[bbox1_key[j]].x
                    var x2 = bbox1[bbox1_key[j + 1]].x
                    var y1 = bbox1[bbox1_key[j]].y
                    var y2 = bbox1[bbox1_key[j + 1]].y
                    var cross_prod = (bbox2[bbox2_key[i]].x - x1) * (y2 - y1) - (bbox2[bbox2_key[i]].y - y1) * (x2 - x1);
                    var sign = (cross_prod >= 0) ? true : false;
                    sign_check.push(sign);
                }
                if (sign_check.length === 4 && (sign_check.every((val, index) => val === [true, true, true, true][index]) ||
                    sign_check.every((val, index) => val === [false, false, false, false][index]))) {
                    intersection.push([bbox2[bbox2_key[i]].x.toFixed(6), bbox2[bbox2_key[i]].y.toFixed(6)]);
                    //intersection.push([bbox2[bbox2_key[i]].x, bbox2[bbox2_key[i]].y]);
                }
            }
        }
        intersection = intersection.filter((item, index, self) =>
            index === self.findIndex((t) => JSON.stringify(t) === JSON.stringify(item))
        );
        // sort intersection points
        if (intersection.length > 2) {
            var intersection_angle = [];
            var intersection_distance = [];

            let minIndex = intersection.reduce((minIdx, currentValue, currentIndex, array) => {
                if (currentValue[0] < array[minIdx][0]) {
                    return currentIndex;
                } else if (currentValue[0] === array[minIdx][0]) {
                    return currentValue[1] < array[minIdx][1] ? currentIndex : minIdx;
                } else {
                    return minIdx;
                }
            }, 0);
            var temp_intersection = intersection.filter((_, index) => index !== minIndex);
            for (let i = 0; i < temp_intersection.length; i++) {
                intersection_angle.push(Math.atan2(temp_intersection[i][1] - intersection[minIndex][1], temp_intersection[i][0] - intersection[minIndex][0]));
                intersection_distance.push((temp_intersection[i][1] - intersection[minIndex][1]) ** 2 + (temp_intersection[i][0] - intersection[minIndex][0]) ** 2);
            }
            let combined = temp_intersection.map((point, index) => ({ // sort intersect coordinates based on angle and then distance
                point: point,
                angle: intersection_angle[index],
                distance: intersection_distance[index]
            }));
            combined.sort((a, b) => a.angle - b.angle || a.distance - b.distance);
            let sorted_intersection = combined.map(item => item.point);
            sorted_intersection.unshift(intersection[minIndex]);
            sorted_intersection.push(intersection[minIndex]);
            // shoelace algorithm to calculate the intersect area
            var area = 0;
            for (let i = 0; i < sorted_intersection.length - 1; i++) {
                area += ((sorted_intersection[i][0] * sorted_intersection[i + 1][1])
                    - (sorted_intersection[i + 1][0] * sorted_intersection[i][1]))
            }
            return area;
        }
        return 0;
    }
    function calculateIOU(bbox1, bbox2, intersectArea) {
        if (intersectArea !== 0) {
            var lot_area1 = (bbox1["vertex1"].x * bbox1["vertex2"].y) + (bbox1["vertex2"].x * bbox1["vertex3"].y)
                + (bbox1["vertex3"].x * bbox1["vertex4"].y) + (bbox1["vertex4"].x * bbox1["vertex_0"].y)
                - ((bbox1["vertex2"].x * bbox1["vertex1"].y) + (bbox1["vertex3"].x * bbox1["vertex2"].y)
                    + (bbox1["vertex4"].x * bbox1["vertex3"].y) + (bbox1["vertex_0"].x * bbox1["vertex4"].y));

            var lot_area2 = (bbox2["vertex1"].x * bbox2["vertex2"].y) + (bbox2["vertex2"].x * bbox2["vertex3"].y)
                + (bbox2["vertex3"].x * bbox2["vertex4"].y) + (bbox2["vertex4"].x * bbox2["vertex_0"].y)
                - ((bbox2["vertex2"].x * bbox2["vertex1"].y) + (bbox2["vertex3"].x * bbox2["vertex2"].y)
                    + (bbox2["vertex4"].x * bbox2["vertex3"].y) + (bbox2["vertex_0"].x * bbox2["vertex4"].y));
            return intersectArea / (lot_area1 + lot_area2 - intersectArea)
        }
        return 0;
    }
    function calculateIOR(bbox1, intersectArea) {
        if (intersectArea !== 0) {
            var lot_area = (bbox1["vertex1"].x * bbox1["vertex2"].y) + (bbox1["vertex2"].x * bbox1["vertex3"].y)
                + (bbox1["vertex3"].x * bbox1["vertex4"].y) + (bbox1["vertex4"].x * bbox1["vertex_0"].y)
                - ((bbox1["vertex2"].x * bbox1["vertex1"].y) + (bbox1["vertex3"].x * bbox1["vertex2"].y)
                    + (bbox1["vertex4"].x * bbox1["vertex3"].y) + (bbox1["vertex_0"].x * bbox1["vertex4"].y));
            return intersectArea / lot_area;
        }
        return 0;
    }

    var occupied = [];
    var current_index = -1;
    // initialize lots to no object
    for (let key1 in payload["lots"]) {
        if (payload["lots"].hasOwnProperty(key1)) {
            occupied.push({ "occupied": 0 });
        }
    }
    for (let key1 in payload["objects"]) {
        current_index++;
        if (payload["objects"].hasOwnProperty(key1)) { // find all lots within the object centroid distance threshold
            var objectCentroid = getBboxCentroid(payload["objects"][key1]);
            var potential_region = [];
            for (let key2 in payload["lots"]) {
                if (payload["lots"].hasOwnProperty(key2)) {
                    var regionCentroid = getBboxCentroid(payload["lots"][key2]);
                    var euclideanDistance = calculateEuclideanDistance(objectCentroid, regionCentroid);
                    potential_region.push(euclideanDistance);
                }
            }
            // calculate IOU or IOR
            if (object_counter) {
                for (let i = 0; i < potential_region.length; i++) {
                    if (potential_region[i] < distance_threshold) {
                        var intersectRegion = calculateIntersectArea(payload["objects"][key1], payload["lots"][i]);
                        var IOR = 0;
                        if (intersection_type == "region") {
                            IOR = calculateIOR(payload["lots"][i], intersectRegion)
                        }
                        else if (intersection_type == "object") {
                            IOR = calculateIOR(payload["objects"][key1], intersectRegion)
                        }
                        if (IOR > intersection_threshold) {
                            occupied[i]["occupied"] += 1;
                            var keyData = Object.keys(payload["objects_extra"][current_index]);
                            var numberData = keyData.length;
                            for (let j = 0; j < numberData; j++) {
                                if (!occupied[i]["extra"]) {
                                    occupied[i]["extra"] = {};
                                }
                                if (!occupied[i]["extra"][keyData[j]]) {
                                    occupied[i]["extra"][keyData[j]] = [];
                                }
                                occupied[i]["extra"][keyData[j]].push(payload["objects_extra"][current_index][keyData[j]]);
                            }
                        }
                    }
                }
            }
            else { // consider the best lot only
                var best_region_index = potential_region.reduce((minIndex, currentValue, currentIndex, array) =>
                    currentValue < array[minIndex] ? currentIndex : minIndex, 0);
                if (potential_region[best_region_index] < distance_threshold) {
                    var intersectRegion = calculateIntersectArea(payload["objects"][key1], payload["lots"][best_region_index]);
                    var IOR = 0;
                    if (intersection_type == "region") {
                        IOR = calculateIOR(payload["lots"][best_region_index], intersectRegion)
                    }
                    else if (intersection_type == "object") {
                        IOR = calculateIOR(payload["objects"][key1], intersectRegion)
                    }
                    if (IOR > intersection_threshold) {
                        occupied[best_region_index]["occupied"] += 1;
                        var keyData = Object.keys(payload["objects_extra"][current_index]);
                        var numberData = keyData.length;
                        for (let i = 0; i < numberData; i++) {
                            if (!occupied[best_region_index]["extra"]) {
                                occupied[best_region_index]["extra"] = {};
                            }
                            if (!occupied[best_region_index]["extra"][keyData[i]]) {
                                occupied[best_region_index]["extra"][keyData[i]] = [];
                            }
                            occupied[best_region_index]["extra"][keyData[i]].push(payload["objects_extra"][current_index][keyData[i]]);
                        }
                    }
                }
            }
        }
    }
    return occupied;
}

// turn into correct format
function postprocess_output(occupied) {
    var results = {};

    for (let i = 0; i < occupied.length; i++) {
        results = initializeResults(results, occupied, i);
        previous_data = initializePreviousData(results, i);
        if (results["Region " + (i + 1)].hasOwnProperty("id") || previous_data["Region " + (i + 1)].hasOwnProperty("id")) {
            results = handleObjectWithID(results, i);
        }
        else if (results["Region " + (i + 1)]["occupied"] > 0) {
            results = handleOccupiedRegion(results, i);
        }
        else if (previous_data["Region " + (i + 1)]["entry_time"]) {
            results = handleNotOccupiedRegion(results, i);
        }
    }
    return results;
}

function initializeResults(results, occupied, i) {
    const regionKey = "Region " + (i + 1);

    if (!results[regionKey]) {
        results[regionKey] = {};
    }
    results[regionKey]["video_id"] = video_id;
    results[regionKey]["region_id"] = (region_id != -1) ? region_id : i + 1;

    if (occupied[i].hasOwnProperty("occupied")) {
        results[regionKey]["occupied"] = occupied[i].occupied;
    }

    if (occupied[i].hasOwnProperty("extra")) {
        for (let key in occupied[i].extra) {
            results[regionKey][key] = occupied[i].extra[key];
        }
    }
    if (results[regionKey].hasOwnProperty("id") || (previous_data && previous_data.hasOwnProperty(regionKey) && previous_data[regionKey].hasOwnProperty("id"))) {
        results[regionKey]["actual_entry_time"] = [];
        results[regionKey]["actual_dwell_time"] = [];
        results[regionKey]["entry_time"] = [];
        results[regionKey]["dwell_time"] = [];
        results[regionKey]["exit_time"] = [];
    }
    else {
        results["Region " + (i + 1)]["actual_entry_time"] = "";
        results["Region " + (i + 1)]["actual_dwell_time"] = "";
        results["Region " + (i + 1)]["entry_time"] = "";
        results["Region " + (i + 1)]["dwell_time"] = "";
        results["Region " + (i + 1)]["exit_time"] = "";
        results["Region " + (i + 1)]["occupancy_check"] = 0;
    }
    return results;
}

function initializePreviousData(results, i) {
    const regionKey = "Region " + (i + 1);

    // Ensure previous_data is set up for this region.
    if (!previous_data) {
        previous_data = {};
    }
    if (results[regionKey].hasOwnProperty("id")) {
        if (!previous_data[regionKey]) {
            previous_data[regionKey] = {};
            previous_data[regionKey]["id"] = [];
            previous_data[regionKey]["actual_entry_time"] = [];
            previous_data[regionKey]["actual_dwell_time"] = [];
            previous_data[regionKey]["entry_time"] = [];
            previous_data[regionKey]["dwell_time"] = [];
            previous_data[regionKey]["exit_time"] = [];
        }
    }
    else {
        if (!previous_data.hasOwnProperty(regionKey)) {
            previous_data[regionKey] = results[regionKey];
            previous_data[regionKey]["actual_entry_time"] = "";
            previous_data[regionKey]["actual_dwell_time"] = "";
            previous_data[regionKey]["entry_time"] = "";
            previous_data[regionKey]["dwell_time"] = "";
            previous_data[regionKey]["exit_time"] = "";
            previous_data[regionKey]["occupancy_check"] = 0;
        }
    }

    return previous_data;
}

function handleObjectWithID(results, i) {
    const regionKey = "Region " + (i + 1);
    const resultIds = results[regionKey]["id"];
    const previousDataIds = previous_data[regionKey]["id"];

    if (!resultIds && !previousDataIds) {
        return results;
    }

    if (resultIds) {
        for (let j = 0; j < resultIds.length; j++) {
            // Check existing id data
            let prevIndex = -1;

            if (previous_data[regionKey]["id"] && previous_data[regionKey]["id"].length > 0) {
                prevIndex = previous_data[regionKey]["id"].indexOf(resultIds[j]);
            }

            if (prevIndex === -1) {
                results[regionKey]["actual_entry_time"].push(msg.nodered_timestamp);
                results[regionKey]["entry_time"].push(msg.object_timestamp);
                results[regionKey]["actual_dwell_time"].push("00:00:00");
                results[regionKey]["dwell_time"].push(0);
                results[regionKey]["exit_time"].push("");
            } else {
                results[regionKey]["actual_entry_time"].push(previous_data[regionKey]["actual_entry_time"][prevIndex]);
                results[regionKey]["entry_time"].push(previous_data[regionKey]["entry_time"][prevIndex]);

                // Calculate dwell time.
                const dwell_timestamp = (msg.object_timestamp - results[regionKey]["entry_time"][j]);
                const dwell_hours = Math.floor(dwell_timestamp / 3600).toString().padStart(2, "0");
                const dwell_mins = Math.floor((dwell_timestamp / 60) % 60).toString().padStart(2, "0");
                const dwell_seconds = Math.floor(dwell_timestamp % 60).toString().padStart(2, "0");
                results[regionKey]["dwell_time"].push(dwell_timestamp);
                results[regionKey]["actual_dwell_time"].push(dwell_hours + ":" + dwell_mins + ":" + dwell_seconds);
                results[regionKey]["exit_time"].push("");
            }
        }
    }

    if (previousDataIds) {
        for (let j = 0; j < previousDataIds.length; j++) {
            let prevIndex = -1;

            if (!results[regionKey]["id"]) {
                results[regionKey] = previous_data[regionKey];
                results[regionKey]["occupied"] = 0;
                break;
            }
            else {
                if (!results[regionKey]["dwell_time"].some(val => val >= stop_duration)) {
                    results[regionKey]["occupied"] = 0;
                }

                prevIndex = results[regionKey]["id"].indexOf(previousDataIds[j]);
                if (prevIndex === -1) {
                    let keyList = Object.keys(previous_data[regionKey]);
                    for (let k = 0; k < keyList.length; k++) {
                        if (keyList[k] == "video_id" || keyList[k] == "region_id" || keyList[k] == "occupied") {
                            continue;
                        }
                        results[regionKey][keyList[k]].push(previous_data[regionKey][keyList[k]][j]);
                    }
                }
            }
        }
    }
    return results;
}

function handleOccupiedRegion(results, i) {
    const regionKey = "Region " + (i + 1);
    if (previous_data[regionKey]["entry_time"] == "") {
        results[regionKey]["actual_entry_time"] = msg.nodered_timestamp;
        results[regionKey]["entry_time"] = msg.object_timestamp;
    }
    else {
        if (previous_data[regionKey]["occupancy_check"] >= occupancy_check_duration) {
            results[regionKey]["actual_dwell_time"] = "00:00:00";
            results[regionKey]["dwell_time"] = 0;
            results[regionKey]["actual_entry_time"] = msg.nodered_timestamp;
            results[regionKey]["entry_time"] = msg.object_timestamp;
        }
        else {
            results[regionKey]["actual_entry_time"] = previous_data[regionKey]["actual_entry_time"];
            results[regionKey]["entry_time"] = previous_data[regionKey]["entry_time"];
        }

        var dwell_timestamp = msg.object_timestamp - results[regionKey]["entry_time"];
        var dwell_hours = Math.floor(dwell_timestamp / 3600).toString();
        dwell_hours = dwell_hours.toString().length == 1 ? dwell_hours.toString().padStart(2, "0") : dwell_hours.toString();
        var dwell_mins = Math.floor((dwell_timestamp / 60) % 60).toString().padStart(2, "0");
        var dwell_seconds = Math.floor(dwell_timestamp % 60).toString().padStart(2, "0");

        results[regionKey]["dwell_time"] = dwell_timestamp;
        results[regionKey]["actual_dwell_time"] = dwell_hours + ":" + dwell_mins + ":" + dwell_seconds;
        results[regionKey]["occupancy_check"] = 0;
    }

    return results;
}

function handleNotOccupiedRegion(results, i) {
    const regionKey = "Region " + (i + 1);

    results[regionKey]["actual_entry_time"] = previous_data[regionKey]["actual_entry_time"];
    results[regionKey]["entry_time"] = previous_data[regionKey]["entry_time"];
    results[regionKey]["actual_dwell_time"] = previous_data[regionKey]["actual_dwell_time"];
    results[regionKey]["dwell_time"] = previous_data[regionKey]["dwell_time"];
    results[regionKey]["occupancy_check"] = previous_data[regionKey]["occupancy_check"] + 1;

    if (previous_data[regionKey]["exit_time"]) {
        results[regionKey]["exit_time"] = previous_data[regionKey]["exit_time"];
    }
    else {
        results[regionKey]["exit_time"] = msg.nodered_timestamp;
    }
    return results;
}

function main(payload, distance_threshold = 100, intersection_type = "region", intersection_threshold = 0.5, object_counter = false) {
    var results = preprocess_bboxes(payload);
    var occupied = euclidean_iou_function(results, distance_threshold, intersection_type, intersection_threshold, object_counter);
    var out = postprocess_output(occupied);
    flow.set(previous_data_name, out);
    return out;
}

// user-defined parameters
var distance_threshold = flow.get("distance_threshold");
var intersection_type = flow.get("intersection_type");
var intersection_threshold = flow.get("intersection_threshold");
var object_counter = flow.get("object_counter");

msg.payload = main(payload, distance_threshold, intersection_type, intersection_threshold, object_counter);
flow.set(detection_name, msg);
return msg;

let region_name = "predefined_regions_4";
let detection_name = "object_detection_4";
let previous_data_name = "previous_data_4";
let occupancy_check_duration = 60;
let video_id = 4;
let region_id = -1; //Recommend to set this only when there is 1 predefined region in the video

var payload = msg.payload;
var previous_data = flow.get(previous_data_name);
var stop_duration = flow.get("stop_duration");

// preparing bboxes
function preprocess_bboxes(payload) {
    function getObjectData(object_data) {
        var current_object_data = {};
        if (object_data.hasOwnProperty("type")) {
            current_object_data["type"] = object_data["type"];
        }
        if (object_data.hasOwnProperty("color")) {
            current_object_data["color"] = object_data["color"];
        }
        if (object_data.hasOwnProperty("license_plate")) {
            current_object_data["license_plate"] = object_data["license_plate"];
        }
        if (object_data.hasOwnProperty("id")) {
            current_object_data["id"] = object_data["id"];
        }
        if (object_data.hasOwnProperty("roi_type")) {
            current_object_data["roi_type"] = object_data["roi_type"];
        }
        return current_object_data;
    }
    function getLotBbox(lot_data) {
        return { "vertex1": lot_data.vertex1, "vertex2": lot_data.vertex2, "vertex3": lot_data.vertex3, "vertex4": lot_data.vertex4, "vertex_0": lot_data.vertex1 };
    }

    var results = {};
    var objects = [];
    var objects_extra = [];
    var lots = [];
    // get all objects bboxes
    for (let key in payload) {
        if (payload.hasOwnProperty(key)) {
            var objectData = getObjectData(payload[key]);
            if (objectData !== null) {
                objects_extra.push(
                    objectData
                );
                objects.push(
                    payload[key]["bbox"]
                );
            }
        }
    }
    // get all lots bboxes
    var predefined_regions = flow.get(region_name);
    var keys = Object.keys(predefined_regions);

    for (let i = 0; i < keys.length; i++) {
        if (predefined_regions.hasOwnProperty(keys[i]) && keys[i] != "_msgid") {
            lots.push(
                getLotBbox(predefined_regions[keys[i]])
            );
        }
    }

    results["objects"] = objects;
    results["objects_extra"] = objects_extra;
    results["lots"] = lots;
    return results;
}

// euclidean distance and iou check
function euclidean_iou_function(payload, distance_threshold = 100, intersection_type = "region", intersection_threshold = 0.5, object_counter = false) {
    function getBboxCentroid(bbox) {
        var centroid_x = (bbox["vertex1"].x + bbox["vertex2"].x + bbox["vertex3"].x + bbox["vertex4"].x) / 4;
        var centroid_y = (bbox["vertex1"].y + bbox["vertex2"].y + bbox["vertex3"].y + bbox["vertex4"].y) / 4;
        return [centroid_x, centroid_y];
    }
    function calculateEuclideanDistance(centroid1, centroid2) {
        return Math.sqrt((centroid2[0] - centroid1[0]) ** 2 + (centroid2[1] - centroid1[1]) ** 2);
    }
    function calculateIntersectArea(bbox1, bbox2) { // objects, lots
        var bbox1_key = Object.keys(bbox1)
        var bbox2_key = Object.keys(bbox2)
        var intersection = [];

        for (let i = 0; i < bbox1_key.length - 1; i++) {
            for (let j = 0; j < bbox2_key.length - 1; j++) {
                var A1 = bbox1[bbox1_key[i + 1]].y - bbox1[bbox1_key[i]].y; // Ax + By + C = 0
                var B1 = bbox1[bbox1_key[i]].x - bbox1[bbox1_key[i + 1]].x;
                var C1 = - A1 * bbox1[bbox1_key[i]].x - B1 * bbox1[bbox1_key[i]].y;

                var A2 = bbox2[bbox2_key[j + 1]].y - bbox2[bbox2_key[j]].y;
                var B2 = bbox2[bbox2_key[j]].x - bbox2[bbox2_key[j + 1]].x;
                var C2 = - A2 * bbox2[bbox2_key[j]].x - B2 * bbox2[bbox2_key[j]].y;

                // parallel lines case
                if (A1 * B2 == A2 * B1) {
                    // coincide case
                    if ((A1 * C2 == A2 * C1) && (B1 * C2 == B2 * C1)) {
                        if (B1 == 0 && A1 == 0) { // one point bbox case
                            intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                        }
                        else if (B2 == 0 && A2 == 0) { // one point bbox case
                            intersection.push([bbox2[bbox2_key[i]].x.toFixed(6), bbox2[bbox2_key[i]].y.toFixed(6)]);
                        }
                        else if (B1 == 0) { // vertical lines case
                            var y_min1 = (bbox1[bbox1_key[i]].y < bbox1[bbox1_key[i + 1]].y) ? bbox1[bbox1_key[i]].y : bbox1[bbox1_key[i + 1]].y;
                            var y_max1 = (bbox1[bbox1_key[i]].y < bbox1[bbox1_key[i + 1]].y) ? bbox1[bbox1_key[i + 1]].y : bbox1[bbox1_key[i]].y;
                            var y_min2 = (bbox2[bbox2_key[i]].y < bbox2[bbox2_key[i + 1]].y) ? bbox2[bbox2_key[i]].y : bbox2[bbox2_key[i + 1]].y;
                            var y_max2 = (bbox2[bbox2_key[i]].y < bbox2[bbox2_key[i + 1]].y) ? bbox2[bbox2_key[i + 1]].y : bbox2[bbox2_key[i]].y;
                            if ((y_min1 < y_max2) && (y_min2 < y_max1)) {
                                var y_min = (y_min1 < y_min2) ? y_min2 : y_min1;
                                var y_max = (y_max1 < y_max2) ? y_max1 : y_max2;
                                intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), y_min.toFixed(6)]);
                                intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), y_max.toFixed(6)]);
                            }
                        }
                        else if (A1 == 0) { // horizontal lines case
                            var x_min1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i]].x : bbox1[bbox1_key[i + 1]].x;
                            var x_max1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i + 1]].x : bbox1[bbox1_key[i]].x;
                            var x_min2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i]].x : bbox2[bbox2_key[i + 1]].x;
                            var x_max2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i + 1]].x : bbox2[bbox2_key[i]].x;
                            if ((x_min1 < x_max2) && (x_min2 < x_max1)) {
                                var x_min = (x_min1 < x_min2) ? x_min2 : x_min1;
                                var x_max = (x_max1 < x_max2) ? x_max1 : x_max2;
                                intersection.push([x_min.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                                intersection.push([x_max.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                            }
                        }
                        else {
                            var x_min1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i]].x : bbox1[bbox1_key[i + 1]].x;
                            var x_max1 = (bbox1[bbox1_key[i]].x < bbox1[bbox1_key[i + 1]].x) ? bbox1[bbox1_key[i + 1]].x : bbox1[bbox1_key[i]].x;
                            var x_min2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i]].x : bbox2[bbox2_key[i + 1]].x;
                            var x_max2 = (bbox2[bbox2_key[i]].x < bbox2[bbox2_key[i + 1]].x) ? bbox2[bbox2_key[i + 1]].x : bbox2[bbox2_key[i]].x;
                            if ((x_min1 < x_max2) && (x_min2 < x_max1) && (y_min1 < y_max2) && (y_min2 < y_max1)) {
                                var x_min = (x_min1 < x_min2) ? x_min2 : x_min1;
                                var x_max = (x_max1 < x_max2) ? x_max1 : x_max2;
                                var xY_min = (-A1 * x_min - C1) / B1
                                var xY_max = (-A1 * x_max - C1) / B1
                                intersection.push([x_min.toFixed(6), xY_min.toFixed(6)]);
                                intersection.push([x_max.toFixed(6), xY_max.toFixed(6)]);
                            }
                        }
                    }
                }
                else {
                    // get intersection coordinates
                    var x1 = bbox1[bbox1_key[i]].x;
                    var x2 = bbox1[bbox1_key[i + 1]].x;
                    var x3 = bbox2[bbox2_key[j]].x;
                    var x4 = bbox2[bbox2_key[j + 1]].x;
                    var y1 = bbox1[bbox1_key[i]].y;
                    var y2 = bbox1[bbox1_key[i + 1]].y;
                    var y3 = bbox2[bbox2_key[j]].y;
                    var y4 = bbox2[bbox2_key[j + 1]].y;

                    var den = (x2 - x1) * (y3 - y4) - (y2 - y1) * (x3 - x4);
                    var t = ((x3 - x1) * (y3 - y4) - (y3 - y1) * (x3 - x4)) / den;
                    var u = ((x3 - x1) * (y1 - y2) - (y3 - y1) * (x1 - x2)) / den;
                    if ((t >= 0) && (t <= 1) && (u >= 0) && (u <= 1)) { // intersect happens if this condition is fulfilled
                        var ix = x1 + t * (x2 - x1);
                        var iy = y1 + t * (y2 - y1);
                        intersection.push([ix.toFixed(6), iy.toFixed(6)]);
                    }
                }
            }
        }
        // get clipped coordinates
        var specialcase1 = true;
        var specialcase2 = false;
        var temp1 = bbox1[bbox1_key[0]];
        var temp2 = bbox2[bbox2_key[0]];
        for (let i = 1; i < bbox1_key.length - 1; i++) { // special case
            if (temp1 !== bbox1[bbox1_key[i]]) {
                specialcase1 = false;
                break;
            }
        }
        for (let i = 1; i < bbox2_key.length - 1; i++) {
            if (temp2 !== bbox2[bbox2_key[i]]) {
                specialcase2 = false;
                break;
            }
        }
        if (!(specialcase2)) {
            for (let i = 0; i < bbox1_key.length - 1; i++) {
                var sign_check = [];
                for (let j = 0; j < bbox2_key.length - 1; j++) {
                    var x1 = bbox2[bbox2_key[j]].x
                    var x2 = bbox2[bbox2_key[j + 1]].x
                    var y1 = bbox2[bbox2_key[j]].y
                    var y2 = bbox2[bbox2_key[j + 1]].y
                    var cross_prod = (bbox1[bbox1_key[i]].x - x1) * (y2 - y1) - (bbox1[bbox1_key[i]].y - y1) * (x2 - x1);
                    var sign = (cross_prod >= 0) ? true : false;
                    sign_check.push(sign);
                }
                if (sign_check.length === 4 && (sign_check.every((val, index) => val === [true, true, true, true][index]) ||
                    sign_check.every((val, index) => val === [false, false, false, false][index]))) { // similar signs indicate the coordinate is within another bbox
                    intersection.push([bbox1[bbox1_key[i]].x.toFixed(6), bbox1[bbox1_key[i]].y.toFixed(6)]);
                    //intersection.push([bbox1[bbox1_key[i]].x, bbox1[bbox1_key[i]].y]);
                }
            }
        }
        if (!(specialcase1)) {
            for (let i = 0; i < bbox2_key.length - 1; i++) { // similar purpose but for second bbox
                var sign_check = [];
                for (let j = 0; j < bbox1_key.length - 1; j++) {
                    var x1 = bbox1[bbox1_key[j]].x
                    var x2 = bbox1[bbox1_key[j + 1]].x
                    var y1 = bbox1[bbox1_key[j]].y
                    var y2 = bbox1[bbox1_key[j + 1]].y
                    var cross_prod = (bbox2[bbox2_key[i]].x - x1) * (y2 - y1) - (bbox2[bbox2_key[i]].y - y1) * (x2 - x1);
                    var sign = (cross_prod >= 0) ? true : false;
                    sign_check.push(sign);
                }
                if (sign_check.length === 4 && (sign_check.every((val, index) => val === [true, true, true, true][index]) ||
                    sign_check.every((val, index) => val === [false, false, false, false][index]))) {
                    intersection.push([bbox2[bbox2_key[i]].x.toFixed(6), bbox2[bbox2_key[i]].y.toFixed(6)]);
                    //intersection.push([bbox2[bbox2_key[i]].x, bbox2[bbox2_key[i]].y]);
                }
            }
        }
        intersection = intersection.filter((item, index, self) =>
            index === self.findIndex((t) => JSON.stringify(t) === JSON.stringify(item))
        );
        // sort intersection points
        if (intersection.length > 2) {
            var intersection_angle = [];
            var intersection_distance = [];

            let minIndex = intersection.reduce((minIdx, currentValue, currentIndex, array) => {
                if (currentValue[0] < array[minIdx][0]) {
                    return currentIndex;
                } else if (currentValue[0] === array[minIdx][0]) {
                    return currentValue[1] < array[minIdx][1] ? currentIndex : minIdx;
                } else {
                    return minIdx;
                }
            }, 0);
            var temp_intersection = intersection.filter((_, index) => index !== minIndex);
            for (let i = 0; i < temp_intersection.length; i++) {
                intersection_angle.push(Math.atan2(temp_intersection[i][1] - intersection[minIndex][1], temp_intersection[i][0] - intersection[minIndex][0]));
                intersection_distance.push((temp_intersection[i][1] - intersection[minIndex][1]) ** 2 + (temp_intersection[i][0] - intersection[minIndex][0]) ** 2);
            }
            let combined = temp_intersection.map((point, index) => ({ // sort intersect coordinates based on angle and then distance
                point: point,
                angle: intersection_angle[index],
                distance: intersection_distance[index]
            }));
            combined.sort((a, b) => a.angle - b.angle || a.distance - b.distance);
            let sorted_intersection = combined.map(item => item.point);
            sorted_intersection.unshift(intersection[minIndex]);
            sorted_intersection.push(intersection[minIndex]);
            // shoelace algorithm to calculate the intersect area
            var area = 0;
            for (let i = 0; i < sorted_intersection.length - 1; i++) {
                area += ((sorted_intersection[i][0] * sorted_intersection[i + 1][1])
                    - (sorted_intersection[i + 1][0] * sorted_intersection[i][1]))
            }
            return area;
        }
        return 0;
    }
    function calculateIOU(bbox1, bbox2, intersectArea) {
        if (intersectArea !== 0) {
            var lot_area1 = (bbox1["vertex1"].x * bbox1["vertex2"].y) + (bbox1["vertex2"].x * bbox1["vertex3"].y)
                + (bbox1["vertex3"].x * bbox1["vertex4"].y) + (bbox1["vertex4"].x * bbox1["vertex_0"].y)
                - ((bbox1["vertex2"].x * bbox1["vertex1"].y) + (bbox1["vertex3"].x * bbox1["vertex2"].y)
                    + (bbox1["vertex4"].x * bbox1["vertex3"].y) + (bbox1["vertex_0"].x * bbox1["vertex4"].y));

            var lot_area2 = (bbox2["vertex1"].x * bbox2["vertex2"].y) + (bbox2["vertex2"].x * bbox2["vertex3"].y)
                + (bbox2["vertex3"].x * bbox2["vertex4"].y) + (bbox2["vertex4"].x * bbox2["vertex_0"].y)
                - ((bbox2["vertex2"].x * bbox2["vertex1"].y) + (bbox2["vertex3"].x * bbox2["vertex2"].y)
                    + (bbox2["vertex4"].x * bbox2["vertex3"].y) + (bbox2["vertex_0"].x * bbox2["vertex4"].y));
            return intersectArea / (lot_area1 + lot_area2 - intersectArea)
        }
        return 0;
    }
    function calculateIOR(bbox1, intersectArea) {
        if (intersectArea !== 0) {
            var lot_area = (bbox1["vertex1"].x * bbox1["vertex2"].y) + (bbox1["vertex2"].x * bbox1["vertex3"].y)
                + (bbox1["vertex3"].x * bbox1["vertex4"].y) + (bbox1["vertex4"].x * bbox1["vertex_0"].y)
                - ((bbox1["vertex2"].x * bbox1["vertex1"].y) + (bbox1["vertex3"].x * bbox1["vertex2"].y)
                    + (bbox1["vertex4"].x * bbox1["vertex3"].y) + (bbox1["vertex_0"].x * bbox1["vertex4"].y));
            return intersectArea / lot_area;
        }
        return 0;
    }

    var occupied = [];
    var current_index = -1;
    // initialize lots to no object
    for (let key1 in payload["lots"]) {
        if (payload["lots"].hasOwnProperty(key1)) {
            occupied.push({ "occupied": 0 });
        }
    }
    for (let key1 in payload["objects"]) {
        current_index++;
        if (payload["objects"].hasOwnProperty(key1)) { // find all lots within the object centroid distance threshold
            var objectCentroid = getBboxCentroid(payload["objects"][key1]);
            var potential_region = [];
            for (let key2 in payload["lots"]) {
                if (payload["lots"].hasOwnProperty(key2)) {
                    var regionCentroid = getBboxCentroid(payload["lots"][key2]);
                    var euclideanDistance = calculateEuclideanDistance(objectCentroid, regionCentroid);
                    potential_region.push(euclideanDistance);
                }
            }
            // calculate IOU or IOR
            if (object_counter) {
                for (let i = 0; i < potential_region.length; i++) {
                    if (potential_region[i] < distance_threshold) {
                        var intersectRegion = calculateIntersectArea(payload["objects"][key1], payload["lots"][i]);
                        var IOR = 0;
                        if (intersection_type == "region") {
                            IOR = calculateIOR(payload["lots"][i], intersectRegion)
                        }
                        else if (intersection_type == "object") {
                            IOR = calculateIOR(payload["objects"][key1], intersectRegion)
                        }
                        if (IOR > intersection_threshold) {
                            occupied[i]["occupied"] += 1;
                            var keyData = Object.keys(payload["objects_extra"][current_index]);
                            var numberData = keyData.length;
                            for (let j = 0; j < numberData; j++) {
                                if (!occupied[i]["extra"]) {
                                    occupied[i]["extra"] = {};
                                }
                                if (!occupied[i]["extra"][keyData[j]]) {
                                    occupied[i]["extra"][keyData[j]] = [];
                                }
                                occupied[i]["extra"][keyData[j]].push(payload["objects_extra"][current_index][keyData[j]]);
                            }
                        }
                    }
                }
            }
            else { // consider the best lot only
                var best_region_index = potential_region.reduce((minIndex, currentValue, currentIndex, array) =>
                    currentValue < array[minIndex] ? currentIndex : minIndex, 0);
                if (potential_region[best_region_index] < distance_threshold) {
                    var intersectRegion = calculateIntersectArea(payload["objects"][key1], payload["lots"][best_region_index]);
                    var IOR = 0;
                    if (intersection_type == "region") {
                        IOR = calculateIOR(payload["lots"][best_region_index], intersectRegion)
                    }
                    else if (intersection_type == "object") {
                        IOR = calculateIOR(payload["objects"][key1], intersectRegion)
                    }
                    if (IOR > intersection_threshold) {
                        occupied[best_region_index]["occupied"] += 1;
                        var keyData = Object.keys(payload["objects_extra"][current_index]);
                        var numberData = keyData.length;
                        for (let i = 0; i < numberData; i++) {
                            if (!occupied[best_region_index]["extra"]) {
                                occupied[best_region_index]["extra"] = {};
                            }
                            if (!occupied[best_region_index]["extra"][keyData[i]]) {
                                occupied[best_region_index]["extra"][keyData[i]] = [];
                            }
                            occupied[best_region_index]["extra"][keyData[i]].push(payload["objects_extra"][current_index][keyData[i]]);
                        }
                    }
                }
            }
        }
    }
    return occupied;
}

// turn into correct format
function postprocess_output(occupied) {
    var results = {};

    for (let i = 0; i < occupied.length; i++) {
        results = initializeResults(results, occupied, i);
        previous_data = initializePreviousData(results, i);
        if (results["Region " + (i + 1)].hasOwnProperty("id") || previous_data["Region " + (i + 1)].hasOwnProperty("id")) {
            results = handleObjectWithID(results, i);
        }
        else if (results["Region " + (i + 1)]["occupied"] > 0) {
            results = handleOccupiedRegion(results, i);
        }
        else if (previous_data["Region " + (i + 1)]["entry_time"]) {
            results = handleNotOccupiedRegion(results, i);
        }
    }
    return results;
}

function initializeResults(results, occupied, i) {
    const regionKey = "Region " + (i + 1);

    if (!results[regionKey]) {
        results[regionKey] = {};
    }
    results[regionKey]["video_id"] = video_id;
    results[regionKey]["region_id"] = (region_id != -1) ? region_id : i + 1;

    if (occupied[i].hasOwnProperty("occupied")) {
        results[regionKey]["occupied"] = occupied[i].occupied;
    }

    if (occupied[i].hasOwnProperty("extra")) {
        for (let key in occupied[i].extra) {
            results[regionKey][key] = occupied[i].extra[key];
        }
    }
    if (results[regionKey].hasOwnProperty("id") || (previous_data && previous_data.hasOwnProperty(regionKey) && previous_data[regionKey].hasOwnProperty("id"))) {
        results[regionKey]["actual_entry_time"] = [];
        results[regionKey]["actual_dwell_time"] = [];
        results[regionKey]["entry_time"] = [];
        results[regionKey]["dwell_time"] = [];
        results[regionKey]["exit_time"] = [];
    }
    else {
        results["Region " + (i + 1)]["actual_entry_time"] = "";
        results["Region " + (i + 1)]["actual_dwell_time"] = "";
        results["Region " + (i + 1)]["entry_time"] = "";
        results["Region " + (i + 1)]["dwell_time"] = "";
        results["Region " + (i + 1)]["exit_time"] = "";
        results["Region " + (i + 1)]["occupancy_check"] = 0;
    }
    return results;
}

function initializePreviousData(results, i) {
    const regionKey = "Region " + (i + 1);

    // Ensure previous_data is set up for this region.
    if (!previous_data) {
        previous_data = {};
    }
    if (results[regionKey].hasOwnProperty("id")) {
        if (!previous_data[regionKey]) {
            previous_data[regionKey] = {};
            previous_data[regionKey]["id"] = [];
            previous_data[regionKey]["actual_entry_time"] = [];
            previous_data[regionKey]["actual_dwell_time"] = [];
            previous_data[regionKey]["entry_time"] = [];
            previous_data[regionKey]["dwell_time"] = [];
            previous_data[regionKey]["exit_time"] = [];
        }
    }
    else {
        if (!previous_data.hasOwnProperty(regionKey)) {
            previous_data[regionKey] = results[regionKey];
            previous_data[regionKey]["actual_entry_time"] = "";
            previous_data[regionKey]["actual_dwell_time"] = "";
            previous_data[regionKey]["entry_time"] = "";
            previous_data[regionKey]["dwell_time"] = "";
            previous_data[regionKey]["exit_time"] = "";
            previous_data[regionKey]["occupancy_check"] = 0;
        }
    }

    return previous_data;
}

function handleObjectWithID(results, i) {
    const regionKey = "Region " + (i + 1);
    const resultIds = results[regionKey]["id"];
    const previousDataIds = previous_data[regionKey]["id"];

    if (!resultIds && !previousDataIds) {
        return results;
    }

    if (resultIds) {
        for (let j = 0; j < resultIds.length; j++) {
            // Check existing id data
            let prevIndex = -1;

            if (previous_data[regionKey]["id"] && previous_data[regionKey]["id"].length > 0) {
                prevIndex = previous_data[regionKey]["id"].indexOf(resultIds[j]);
            }

            if (prevIndex === -1) {
                results[regionKey]["actual_entry_time"].push(msg.nodered_timestamp);
                results[regionKey]["entry_time"].push(msg.object_timestamp);
                results[regionKey]["actual_dwell_time"].push("00:00:00");
                results[regionKey]["dwell_time"].push(0);
                results[regionKey]["exit_time"].push("");
            } else {
                results[regionKey]["actual_entry_time"].push(previous_data[regionKey]["actual_entry_time"][prevIndex]);
                results[regionKey]["entry_time"].push(previous_data[regionKey]["entry_time"][prevIndex]);

                // Calculate dwell time.
                const dwell_timestamp = (msg.object_timestamp - results[regionKey]["entry_time"][j]);
                const dwell_hours = Math.floor(dwell_timestamp / 3600).toString().padStart(2, "0");
                const dwell_mins = Math.floor((dwell_timestamp / 60) % 60).toString().padStart(2, "0");
                const dwell_seconds = Math.floor(dwell_timestamp % 60).toString().padStart(2, "0");
                results[regionKey]["dwell_time"].push(dwell_timestamp);
                results[regionKey]["actual_dwell_time"].push(dwell_hours + ":" + dwell_mins + ":" + dwell_seconds);
                results[regionKey]["exit_time"].push("");
            }
        }
    }

    if (previousDataIds) {
        for (let j = 0; j < previousDataIds.length; j++) {
            let prevIndex = -1;

            if (!results[regionKey]["id"]) {
                results[regionKey] = previous_data[regionKey];
                results[regionKey]["occupied"] = 0;
                break;
            }
            else {
                if (!results[regionKey]["dwell_time"].some(val => val >= stop_duration)) {
                    results[regionKey]["occupied"] = 0;
                }

                prevIndex = results[regionKey]["id"].indexOf(previousDataIds[j]);
                if (prevIndex === -1) {
                    let keyList = Object.keys(previous_data[regionKey]);
                    for (let k = 0; k < keyList.length; k++) {
                        if (keyList[k] == "video_id" || keyList[k] == "region_id" || keyList[k] == "occupied") {
                            continue;
                        }
                        results[regionKey][keyList[k]].push(previous_data[regionKey][keyList[k]][j]);
                    }
                }
            }
        }
    }
    return results;
}

function handleOccupiedRegion(results, i) {
    const regionKey = "Region " + (i + 1);
    if (previous_data[regionKey]["entry_time"] == "") {
        results[regionKey]["actual_entry_time"] = msg.nodered_timestamp;
        results[regionKey]["entry_time"] = msg.object_timestamp;
    }
    else {
        if (previous_data[regionKey]["occupancy_check"] >= occupancy_check_duration) {
            results[regionKey]["actual_dwell_time"] = "00:00:00";
            results[regionKey]["dwell_time"] = 0;
            results[regionKey]["actual_entry_time"] = msg.nodered_timestamp;
            results[regionKey]["entry_time"] = msg.object_timestamp;
        }
        else {
            results[regionKey]["actual_entry_time"] = previous_data[regionKey]["actual_entry_time"];
            results[regionKey]["entry_time"] = previous_data[regionKey]["entry_time"];
        }

        var dwell_timestamp = msg.object_timestamp - results[regionKey]["entry_time"];
        var dwell_hours = Math.floor(dwell_timestamp / 3600).toString();
        dwell_hours = dwell_hours.toString().length == 1 ? dwell_hours.toString().padStart(2, "0") : dwell_hours.toString();
        var dwell_mins = Math.floor((dwell_timestamp / 60) % 60).toString().padStart(2, "0");
        var dwell_seconds = Math.floor(dwell_timestamp % 60).toString().padStart(2, "0");

        results[regionKey]["dwell_time"] = dwell_timestamp;
        results[regionKey]["actual_dwell_time"] = dwell_hours + ":" + dwell_mins + ":" + dwell_seconds;
        results[regionKey]["occupancy_check"] = 0;
    }

    return results;
}

function handleNotOccupiedRegion(results, i) {
    const regionKey = "Region " + (i + 1);

    results[regionKey]["actual_entry_time"] = previous_data[regionKey]["actual_entry_time"];
    results[regionKey]["entry_time"] = previous_data[regionKey]["entry_time"];
    results[regionKey]["actual_dwell_time"] = previous_data[regionKey]["actual_dwell_time"];
    results[regionKey]["dwell_time"] = previous_data[regionKey]["dwell_time"];
    results[regionKey]["occupancy_check"] = previous_data[regionKey]["occupancy_check"] + 1;

    if (previous_data[regionKey]["exit_time"]) {
        results[regionKey]["exit_time"] = previous_data[regionKey]["exit_time"];
    }
    else {
        results[regionKey]["exit_time"] = msg.nodered_timestamp;
    }
    return results;
}

function main(payload, distance_threshold = 100, intersection_type = "region", intersection_threshold = 0.5, object_counter = false) {
    var results = preprocess_bboxes(payload);
    var occupied = euclidean_iou_function(results, distance_threshold, intersection_type, intersection_threshold, object_counter);
    var out = postprocess_output(occupied);
    flow.set(previous_data_name, out);
    return out;
}

// user-defined parameters
var distance_threshold = flow.get("distance_threshold");
var intersection_type = flow.get("intersection_type");
var intersection_threshold = flow.get("intersection_threshold");
var object_counter = flow.get("object_counter");

msg.payload = main(payload, distance_threshold, intersection_type, intersection_threshold, object_counter);
flow.set(detection_name, msg);
return msg;

var header = ["Zone ID", "Spot ID", "Status", "Type", "Color", "Entry Time", "Dwell Time"];
var data = ["video_id", "region_id", "occupied", "roi_type", "color", "actual_entry_time", "actual_dwell_time"];
var first_region_name = "Region 1_1 1";

var result = { "payload": {} };
var payload = msg.payload;
var keys = Object.keys(payload);
var stop_duration = flow.get("stop_duration");

if (keys.length == 0) {
    return result;
}

var offset = 0;

for (let i = 0; i < keys.length; i++) {
    var num_data = 1;

    for (let h = 0; h < data.length; h++) {
        var data_value = payload[keys[i]][data[h]];
        if (Array.isArray(data_value) && data_value.length > num_data) {
            num_data = data_value.length;
        }
    }

    for (let h = 0; h < num_data; h++) {

        if (h > 0) {
            offset += 1;
        }

        if (payload[keys[i]].hasOwnProperty("id") && payload[keys[i]]["dwell_time"][h] < stop_duration) {
            if (h > 0) {
                offset -= 1;
            }
            continue;
        }
        var region_name = "Region " + payload[keys[i]][data[0]] + "_" + payload[keys[i]][data[1]] + " " + (i + 1 + offset);
        result.payload[region_name] = {};

        for (let g = 0; g < data.length; g++) {
            var data_value = payload[keys[i]][data[g]];

            if (data[g] == "occupied") {
                if (data_value == 0) {
                    result.payload[region_name][header[g]] = "Empty";
                }
                else {
                    result.payload[region_name][header[g]] = "Occupied";
                }
            }
            else {
                if (Array.isArray(data_value)) {
                    data_value = data_value[h];
                }
                if (typeof data_value === "undefined") {
                    data_value = "";
                }
                result.payload[region_name][header[g]] = data_value;
            }
        }
    }
}

if (Object.keys(result.payload).length == 0) {
    result.payload[first_region_name] = { "Status": "Empty" };
}

return result;
